const title = 'Value Counter Charm';

function onReady(mount, sourceData) {
  function ValueCounter() {
    // Use the schema-defined properties with appropriate defaults
    const [values, setValues] = useDoc("values", []);
    const [count, setCount] = useDoc("count", 0);

    // State for new value input
    const [newValue, setNewValue] = React.useState("");

    // Update count whenever values change
    React.useEffect(() => {
      setCount(values.length);
    }, [values, setCount]);

    // Add a new value to the array
    const handleAddValue = () => {
      if (newValue.trim() !== "") {
        setValues([...values, newValue.trim()]);
        setNewValue("");
      }
    };

    // Remove a value from the array
    const handleRemoveValue = (index) => {
      const newValues = [...values];
      newValues.splice(index, 1);
      setValues(newValues);
    };

    // Clear all values
    const handleClearAll = () => {
      setValues([]);
    };

    return (
      <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-lg">
        <h1 className="text-2xl font-bold text-center mb-6 text-indigo-700">{title}</h1>

        {/* Count display */}
        <div className="mb-6 p-4 bg-indigo-50 rounded-lg text-center">
          <span className="text-lg font-medium">Count: </span>
          <span className="text-2xl font-bold text-indigo-700">{count}</span>
        </div>

        {/* Input form */}
        <div className="flex mb-4">
          <input
            type="text"
            value={newValue}
            onChange={(e) => setNewValue(e.target.value)}
            placeholder="Add a new value"
            className="flex-grow p-2 border border-gray-300 rounded-l-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <button
            onClick={handleAddValue}
            className="px-4 py-2 bg-indigo-600 text-white rounded-r-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
          >
            Add
          </button>
        </div>

        {/* Values list */}
        <div className="mb-4">
          <h2 className="text-lg font-semibold mb-2">Values:</h2>
          {values.length > 0 ? (
            <ul className="border border-gray-200 rounded-lg divide-y">
              {values.map((value, index) => (
                <li key={index} className="flex justify-between items-center p-3 hover:bg-gray-50">
                  <span className="break-all">{value}</span>
                  <button
                    onClick={() => handleRemoveValue(index)}
                    className="ml-2 p-1 text-red-500 hover:text-red-700 rounded-full hover:bg-red-100"
                  >
                    üóëÔ∏è
                  </button>
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-gray-500 text-center p-4 border border-gray-200 rounded-lg">
              No values added yet
            </p>
          )}
        </div>

        {/* Clear all button */}
        {values.length > 0 && (
          <div className="text-center">
            <button
              onClick={handleClearAll}
              className="px-4 py-2 bg-red-500 text-white rounded-lg hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-500"
            >
              Clear All
            </button>
          </div>
        )}
      </div>
    );
  }

  // Render the component
  const root = ReactDOM.createRoot(mount);
  root.render(<ValueCounter />);
}
