function onReady(mount, sourceData) {
  function ValueCounter() {
    // Persistent state from schema
    const [simpleValue2, setSimpleValue2] = useDoc("simpleValue2", { values: [] });
    const [originalData, setOriginalData] = useDoc("originalData", { simpleValue2: {} });
    const [counts, setCounts] = useDoc("counts", {});
    const [totalValues, setTotalValues] = useDoc("totalValues", 0);
    const [uniqueValues, setUniqueValues] = useDoc("uniqueValues", 0);
    const [timestamp, setTimestamp] = useDoc("timestamp", "");

    // UI state
    const [inputValue, setInputValue] = React.useState("");
    const [isAnalyzing, setIsAnalyzing] = React.useState(false);

    // Function to add a new value to the array
    const addValue = () => {
      if (inputValue.trim() === "") return;

      const updatedValues = [...simpleValue2.values, inputValue.trim()];
      setSimpleValue2({ values: updatedValues });
      setInputValue("");
    };

    // Function to remove a value from the array
    const removeValue = (index) => {
      const updatedValues = [...simpleValue2.values];
      updatedValues.splice(index, 1);
      setSimpleValue2({ values: updatedValues });
    };

    // Function to analyze and count values
    const analyzeValues = () => {
      setIsAnalyzing(true);

      // Store original data
      setOriginalData({ simpleValue2: { ...simpleValue2 } });

      // Count occurrences of each value
      const valuesCounts = {};
      const values = simpleValue2.values || [];

      values.forEach(value => {
        valuesCounts[value] = (valuesCounts[value] || 0) + 1;
      });

      // Update state with results
      setCounts(valuesCounts);
      setTotalValues(values.length);
      setUniqueValues(Object.keys(valuesCounts).length);
      setTimestamp(new Date().toISOString());

      setIsAnalyzing(false);
    };

    // Function to clear all data
    const clearAll = () => {
      setSimpleValue2({ values: [] });
      setCounts({});
      setTotalValues(0);
      setUniqueValues(0);
      setTimestamp("");
    };

    return (
      <div className="max-w-4xl mx-auto p-6">
