          <h2 className="text-xl font-semibold mb-4">Input Values</h2>

          <div className="flex mb-4">
            <input
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              className="flex-grow p-2 border rounded-l focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter a value"
            />
            <button
              onClick={addValue}
              className="px-4 py-2 bg-blue-600 text-white rounded-r hover:bg-blue-700 transition"
            >
              Add
            </button>
          </div>

          {/* Values list */}
          <div className="border rounded-md p-4 bg-gray-50 min-h-[100px] max-h-[200px] overflow-y-auto">
            {simpleValue2.values && simpleValue2.values.length > 0 ? (
              <ul className="space-y-2">
                {simpleValue2.values.map((value, index) => (
                  <li key={index} className="flex justify-between items-center p-2 bg-white rounded border">
                    <span>{value}</span>
                    <button 
                      onClick={() => removeValue(index)}
                      className="text-red-500 hover:text-red-700"
                    >
                      ❌
                    </button>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500 text-center py-4">No values added yet</p>
            )}
          </div>

          <div className="mt-4 flex space-x-2">
            <button
              onClick={analyzeValues}
              disabled={!simpleValue2.values || simpleValue2.values.length === 0 || isAnalyzing}
              className={`px-4 py-2 rounded text-white ${
                !simpleValue2.values || simpleValue2.values.length === 0 || isAnalyzing
                  ? "bg-gray-400 cursor-not-allowed"
                  : "bg-green-600 hover:bg-green-700"
              } transition flex-grow`}
            >
              {isAnalyzing ? "Analyzing..." : "Analyze Values"}
            </button>

            <button
              onClick={clearAll}
              className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition"
            >
              Clear All
            </button>
          </div>
        </div>

        {/* Results section */}
        {timestamp && (
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold mb-4">Analysis Results</h2>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
              <div className="bg-blue-50 p-4 rounded-md border border-blue-200">
                <h3 className="text-sm text-blue-700 font-medium">Total Values</h3>
                <p className="text-2xl font-bold">{totalValues}</p>
              </div>

              <div className="bg-green-50 p-4 rounded-md border border-green-200">
                <h3 className="text-sm text-green-700 font-medium">Unique Values</h3>
                <p className="text-2xl font-bold">{uniqueValues}</p>
              </div>

              <div className="bg-purple-50 p-4 rounded-md border border-purple-200">
                <h3 className="text-sm text-purple-700 font-medium">Last Updated</h3>
                <p className="text-sm font-medium">
                  {new Date(timestamp).toLocaleString()}
                </p>
              </div>
            </div>

            <h3 className="font-medium mb-2">Value Counts:</h3>
            <div className="border rounded-md overflow-hidden">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Value</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Count</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Percentage</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {Object.entries(counts).map(([value, count], index) => (
                    <tr key={index} className={index % 2 === 0 ? "bg-white" : "bg-gray-50"}>
                      <td className="px-6 py-4 whitespace-nowrap">{value}</td>
                      <td className="px-6 py-4 whitespace-nowrap">{count}</td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {totalValues > 0 ? ((count / totalValues) * 100).toFixed(1) + "%" : "0%"}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    );
  }

  // Render the component
  const root = ReactDOM.createRoot(mount);
  root.render(<ValueCounter />);
}
