function onLoad() {
  return []; // No additional libraries needed
}

const title = 'SimpleValue2 Viewer';

function ValuesList({ values }) {
  return (
    <div className="mt-4">
      <h2 className="text-lg font-semibold mb-2">Values</h2>
      {values && values.length > 0 ? (
        <ul className="list-disc pl-5 space-y-1">
          {values.map((value, index) => (
            <li key={index} className="text-gray-800">
              {value}
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-gray-500 italic">No values available</p>
      )}
    </div>
  );
}

function LastUpdatedInfo({ timestamp, showTimestamp }) {
  if (!showTimestamp || !timestamp) return null;

  const formattedDate = new Date(timestamp).toLocaleString();

  return (
    <div className="text-sm text-gray-500 mt-2">
      Last updated: {formattedDate}
    </div>
  );
}

function RefreshButton({ onRefresh, isLoading }) {
  return (
    <button
      onClick={onRefresh}
      disabled={isLoading}
      className={`px-4 py-2 rounded text-white ${
        isLoading ? 'bg-blue-300' : 'bg-blue-500 hover:bg-blue-600'
      } transition-colors`}
    >
      {isLoading ? 'Refreshing...' : 'üîÑ Refresh Now'}
    </button>
  );
}

function SettingsPanel({ showTimestamp, setShowTimestamp, refreshInterval, setRefreshInterval }) {
  return (
    <div className="mt-6 p-4 bg-gray-50 rounded-lg border border-gray-200">
      <h3 className="text-md font-semibold mb-3">Display Settings</h3>

      <div className="flex items-center mb-3">
        <input
          type="checkbox"
          id="showTimestamp"
          checked={showTimestamp}
          onChange={(e) => setShowTimestamp(e.target.checked)}
          className="mr-2 h-4 w-4"
        />
        <label htmlFor="showTimestamp" className="text-gray-700">
          Show last updated timestamp
        </label>
      </div>

      <div className="flex flex-col">
        <label htmlFor="refreshInterval" className="text-gray-700 mb-1">
          Auto-refresh interval (seconds)
        </label>
        <input
          type="number"
          id="refreshInterval"
          value={refreshInterval}
          onChange={(e) => setRefreshInterval(Math.max(5, parseInt(e.target.value) || 30))}
          min="5"
          className="border border-gray-300 rounded px-3 py-2 w-32"
        />
      </div>
    </div>
  );
}

function SimpleValue2Viewer() {
  // Access the data from simpleValue2
  const [simpleValue2Data, setSimpleValue2Data] = useReactiveCell(['simplevalue2', 'values'], []);

  // Access display data
  const [displayData, setDisplayData] = useReactiveCell(['displayData', 'values'], []);
  const [lastUpdated, setLastUpdated] = useReactiveCell(['displayData', 'lastUpdated'], '');

  // Access display settings
  const [showTimestamp, setShowTimestamp] = useReactiveCell(['displaySettings', 'showTimestamp'], true);
  const [refreshInterval, setRefreshInterval] = useReactiveCell(['displaySettings', 'refreshInterval'], 30);

  // Local state for UI
  const [isRefreshing, setIsRefreshing] = React.useState(false);

  // Function to update the display data
  const refreshData = React.useCallback(() => {
    setIsRefreshing(true);

    // Update the display data with the current simpleValue2 values
    setDisplayData(simpleValue2Data || []);
    setLastUpdated(new Date().toISOString());

    setTimeout(() => {
      setIsRefreshing(false);
    }, 500);
  }, [simpleValue2Data, setDisplayData, setLastUpdated]);

  // Set up auto-refresh
  React.useEffect(() => {
    refreshData(); // Initial refresh

    const intervalId = setInterval(() => {
      refreshData();
    }, refreshInterval * 1000);

    return () => clearInterval(intervalId);
  }, [refreshData, refreshInterval]);

  // Watch for changes in simpleValue2Data and refresh when it changes
  React.useEffect(() => {
    refreshData();
  }, [simpleValue2Data, refreshData]);

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-lg shadow-md">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-800">SimpleValue2 Viewer</h1>
        <RefreshButton onRefresh={refreshData} isLoading={isRefreshing} />
      </div>

      <div className="mt-4 border-t pt-4">
        <ValuesList values={displayData} />
        <LastUpdatedInfo timestamp={lastUpdated} showTimestamp={showTimestamp} />
      </div>

      <SettingsPanel
        showTimestamp={showTimestamp}
        setShowTimestamp={setShowTimestamp}
        refreshInterval={refreshInterval}
        setRefreshInterval={setRefreshInterval}
      />
    </div>
  );
}

function onReady(mount, sourceData) {
  const root = ReactDOM.createRoot(mount);
  root.render(<SimpleValue2Viewer />);
}
```
