      const updatedValues = [...simpleValue2.values];
      updatedValues.splice(index, 1);
      setSimpleValue2({ ...simpleValue2, values: updatedValues });
    }, [simpleValue2, setSimpleValue2]);

    return (
      <div className="container mx-auto p-4 max-w-3xl">
        <h1 className="text-2xl font-bold mb-4 text-center">{title}</h1>

        {/* Input section */}
        <div className="mb-6 bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-2">Add Values</h2>
          <div className="flex mb-2">
            <input
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              className="flex-grow p-2 border rounded-l focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter a value"
            />
            <button
              onClick={addValue}
              className="bg-blue-500 text-white px-4 py-2 rounded-r hover:bg-blue-600 transition"
            >
              Add
            </button>
          </div>

          <div className="flex justify-between">
            <button
              onClick={countValues}
              disabled={!simpleValue2.values || simpleValue2.values.length === 0}
              className={`bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition ${(!simpleValue2.values || simpleValue2.values.length === 0) ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              {isAnalyzing ? 'Analyzing...' : 'Count Values'}
            </button>

            <button
              onClick={clearValues}
              disabled={!simpleValue2.values || simpleValue2.values.length === 0}
              className={`bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 transition ${(!simpleValue2.values || simpleValue2.values.length === 0) ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              Clear All
            </button>
          </div>
        </div>

        {/* Current values */}
        <div className="mb-6 bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-2">Current Values</h2>
          {simpleValue2.values && simpleValue2.values.length > 0 ? (
            <div className="flex flex-wrap gap-2">
              {simpleValue2.values.map((value, index) => (
                <div key={index} className="bg-gray-100 px-3 py-1 rounded-full flex items-center">
                  <span className="mr-2">{value}</span>
                  <button 
                    onClick={() => removeValue(index)}
                    className="text-red-500 hover:text-red-700"
                  >
                    ❌
                  </button>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-500 italic">No values added yet</p>
          )}
        </div>

        {/* Results section */}
        {timestamp && (
          <div className="bg-white p-4 rounded shadow">
            <h2 className="text-lg font-semibold mb-2">Analysis Results</h2>
            <div className="grid grid-cols-2 gap-4 mb-4">
              <div className="bg-blue-50 p-3 rounded">
                <p className="text-sm text-gray-600">Total Values</p>
                <p className="text-xl font-bold">{totalValues}</p>
              </div>
              <div className="bg-green-50 p-3 rounded">
                <p className="text-sm text-gray-600">Unique Values</p>
                <p className="text-xl font-bold">{uniqueValues}</p>
              </div>
            </div>

            <h3 className="font-medium mb-2">Value Counts</h3>
            {Object.keys(counts).length > 0 ? (
              <div className="overflow-hidden rounded border border-gray-200">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Value</th>
                      <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Count</th>
                      <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Percentage</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {Object.entries(counts).map(([value, count], index) => (
                      <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                        <td className="px-4 py-2 whitespace-nowrap">{value}</td>
                        <td className="px-4 py-2 whitespace-nowrap">{count}</td>
                        <td className="px-4 py-2 whitespace-nowrap">
                          {totalValues > 0 ? `${((count / totalValues) * 100).toFixed(1)}%` : '0%'}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <p className="text-gray-500 italic">No data to display</p>
            )}

            <p className="text-xs text-gray-500 mt-4">
              Last analyzed: {new Date(timestamp).toLocaleString()}
            </p>
          </div>
        )}
      </div>
    );
  }

  const root = ReactDOM.createRoot(mount);
  root.render(<ValueCounter />);
}
