const title = 'Value Counter';

function onLoad() {
  return []; // No additional libraries needed
}

function onReady(mount, sourceData) {
  function ValueCounter() {
    // Persistent state using the schema
    const [simpleValue2, setSimpleValue2] = useDoc("simpleValue2", { values: [] });
    const [originalData, setOriginalData] = useDoc("originalData", { simpleValue2: { values: [] } });
    const [counts, setCounts] = useDoc("counts", {});
    const [totalValues, setTotalValues] = useDoc("totalValues", 0);
    const [uniqueValues, setUniqueValues] = useDoc("uniqueValues", 0);
    const [timestamp, setTimestamp] = useDoc("timestamp", "");

    // UI state
    const [inputValue, setInputValue] = React.useState("");
    const [isLoading, setIsLoading] = React.useState(false);

    // Process the values and count occurrences
    const processValues = React.useCallback(() => {
      setIsLoading(true);

      try {
        // Save original data
        setOriginalData({ simpleValue2: { ...simpleValue2 } });

        const values = simpleValue2.values || [];
        const valueCounts = {};

        // Count occurrences of each value
        values.forEach(value => {
          valueCounts[value] = (valueCounts[value] || 0) + 1;
        });

        // Update the counts and statistics
        setCounts(valueCounts);
        setTotalValues(values.length);
        setUniqueValues(Object.keys(valueCounts).length);
        setTimestamp(new Date().toISOString());
      } catch (error) {
        console.error("Error processing values:", error);
      } finally {
        setIsLoading(false);
      }
    }, [simpleValue2, setCounts, setOriginalData, setTotalValues, setUniqueValues, setTimestamp]);

    // Add a new value to the array
    const addValue = React.useCallback(() => {
      if (inputValue.trim()) {
        const updatedValues = [...(simpleValue2.values || []), inputValue.trim()];
        setSimpleValue2({ ...simpleValue2, values: updatedValues });
        setInputValue("");
      }
    }, [inputValue, simpleValue2, setSimpleValue2]);

    // Clear all values
    const clearValues = React.useCallback(() => {
      setSimpleValue2({ values: [] });
      setCounts({});
      setTotalValues(0);
      setUniqueValues(0);
      setTimestamp("");
    }, [setSimpleValue2, setCounts, setTotalValues, setUniqueValues, setTimestamp]);

    return (
      <div className="container mx-auto p-6 max-w-3xl">
        <h1 className="text-3xl font-bold mb-6 text-center text-blue-700">
          {title}
        </h1>

        {/* Input section */}
        <div className="mb-8 bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4">Add Values</h2>
          <div className="flex gap-2 mb-4">
            <input
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              className="flex-1 px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter a value"
              onKeyPress={(e) => e.key === 'Enter' && addValue()}
            />
            <button
              onClick={addValue}
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition-colors"
            >
              Add
            </button>
          </div>

          <div className="flex gap-2">
            <button
              onClick={processValues}
              disabled={isLoading || (simpleValue2.values || []).length === 0}
              className={`flex-1 px-4 py-2 rounded-md text-white transition-colors ${
                isLoading || (simpleValue2.values || []).length === 0
                  ? "bg-gray-400 cursor-not-allowed"
                  : "bg-green-600 hover:bg-green-700"
              }`}
            >
              {isLoading ? "Processing..." : "Count Values"}
            </button>

            <button
              onClick={clearValues}
              disabled={isLoading || (simpleValue2.values || []).length === 0}
              className={`flex-1 px-4 py-2 rounded-md text-white transition-colors ${
                isLoading || (simpleValue2.values || []).length === 0
                  ? "bg-gray-400 cursor-not-allowed"
                  : "bg-red-600 hover:bg-red-700"
              }`}
            >
              Clear All
            </button>
          </div>
        </div>

        {/* Current values display */}
        <div className="mb-8 bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-4">Current Values</h2>
          {(simpleValue2.values || []).length > 0 ? (
            <div className="flex flex-wrap gap-2">
              {simpleValue2.values.map((value, index) => (
                <span
                  key={index}
                  className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm"
                >
                  {value}
                </span>
              ))}
            </div>
          ) : (
            <p className="text-gray-500 italic">No values added yet</p>
          )}
        </div>

        {/* Results section */}
        {Object.keys(counts).length > 0 && (
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-xl font-semibold mb-4">Value Counts</h2>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
              <div className="bg-gray-50 p-4 rounded-md">
                <h3 className="font-medium text-gray-700 mb-2">Statistics</h3>
                <p className="mb-1"><span className="font-medium">Total values:</span> {totalValues}</p>
                <p className="mb-1"><span className="font-medium">Unique values:</span> {uniqueValues}</p>
                <p><span className="font-medium">Last updated:</span> {new Date(timestamp).toLocaleString()}</p>
              </div>

              <div className="bg-gray-50 p-4 rounded-md">
                <h3 className="font-medium text-gray-700 mb-2">Value Distribution</h3>
                <div className="max-h-40 overflow-y-auto">
                  <table className="w-full">
                    <thead className="text-left">
                      <tr>
                        <th className="pb-2">Value</th>
                        <th className="pb-2">Count</th>
                        <th className="pb-2">Percentage</th>
                      </tr>
                    </thead>
                    <tbody>
                      {Object.entries(counts).map(([value, count]) => (
                        <tr key={value} className="border-t border-gray-200">
                          <td className="py-2">{value}</td>
                          <td className="py-2">{count}</td>
                          <td className="py-2">{((count / totalValues) * 100).toFixed(1)}%</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>

            {/* Visualization */}
            <div className="bg-gray-50 p-4 rounded-md">
              <h3 className="font-medium text-gray-700 mb-2">Visualization</h3>
              <div className="space-y-2">
                {Object.entries(counts).map(([value, count]) => (
                  <div key={value} className="flex items-center">
                    <div className="w-24 truncate mr-2" title={value}>
                      {value}
                    </div>
                    <div className="flex-1">
                      <div 
                        className="bg-blue-500 h-6 rounded-sm flex items-center px-2 text-white text-xs"
                        style={{ width: `${Math.max(5, (count / totalValues) * 100)}%` }}
                      >
                        {count}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
    );
  }

  const root = ReactDOM.createRoot(mount);
  root.render(<ValueCounter />);
}
