    const [counts, setCounts] = useDoc("counts", {});
    const [totalCount, setTotalCount] = useDoc("totalCount", 0);
    const [uniqueCount, setUniqueCount] = useDoc("uniqueCount", 0);

    // Local state for new value input
    const [newValue, setNewValue] = React.useState("");

    // Process the values array and count occurrences
    React.useEffect(() => {
      // Extract the values array
      const values = simpleValue2.values || [];

      // Count occurrences of each unique value
      const valueCounts = {};
      values.forEach(value => {
        valueCounts[value] = (valueCounts[value] || 0) + 1;
      });

      // Update the counts, total count, and unique count
      setCounts(valueCounts);
      setTotalCount(values.length);
      setUniqueCount(Object.keys(valueCounts).length);

      // Update the original data
      setOriginalData({ simpleValue2: { values: [...values] } });
    }, [simpleValue2]);

    // Handle adding a new value
    const handleAddValue = () => {
      if (newValue.trim() === "") return;

      const updatedValues = [...simpleValue2.values, newValue.trim()];
      setSimpleValue2({ values: updatedValues });
      setNewValue("");
    };

    // Handle removing a value
    const handleRemoveValue = (valueToRemove) => {
      const updatedValues = simpleValue2.values.filter(value => value !== valueToRemove);
      setSimpleValue2({ values: updatedValues });
    };

    // Handle clearing all values
    const handleClearValues = () => {
      setSimpleValue2({ values: [] });
    };

    return (
      <div className="container mx-auto p-4 max-w-3xl">
        <h1 className="text-2xl font-bold mb-4">{title}</h1>

        {/* Input section */}
        <div className="mb-6 bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-2">Add Values</h2>
          <div className="flex">
            <input
              type="text"
              value={newValue}
              onChange={(e) => setNewValue(e.target.value)}
              className="flex-grow p-2 border rounded-l focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter a value"
            />
            <button
              onClick={handleAddValue}
              className="bg-blue-500 text-white px-4 py-2 rounded-r hover:bg-blue-600 transition"
            >
              Add
            </button>
          </div>
          <div className="mt-2 flex justify-end">
            <button
              onClick={handleClearValues}
              className="bg-red-500 text-white px-3 py-1 rounded text-sm hover:bg-red-600 transition"
            >
              Clear All
            </button>
          </div>
        </div>

        {/* Current values section */}
        <div className="mb-6 bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-2">Current Values</h2>
          {simpleValue2.values.length > 0 ? (
            <div className="flex flex-wrap gap-2">
              {simpleValue2.values.map((value, index) => (
                <div key={index} className="bg-gray-100 px-3 py-1 rounded-full flex items-center">
                  <span>{value}</span>
                  <button
                    onClick={() => handleRemoveValue(value)}
                    className="ml-2 text-red-500 hover:text-red-700"
                  >
                    ×
                  </button>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-gray-500 italic">No values added yet</p>
          )}
        </div>

        {/* Statistics section */}
        <div className="mb-6 bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-2">Statistics</h2>
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-blue-50 p-3 rounded">
              <p className="text-sm text-gray-600">Total Values</p>
              <p className="text-xl font-bold">{totalCount}</p>
            </div>
            <div className="bg-green-50 p-3 rounded">
              <p className="text-sm text-gray-600">Unique Values</p>
              <p className="text-xl font-bold">{uniqueCount}</p>
            </div>
          </div>
        </div>

        {/* Value counts table */}
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-2">Value Counts</h2>
          {Object.keys(counts).length > 0 ? (
            <div className="overflow-x-auto">
              <table className="min-w-full bg-white">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="py-2 px-4 text-left">Value</th>
                    <th className="py-2 px-4 text-left">Count</th>
                    <th className="py-2 px-4 text-left">Percentage</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(counts).map(([value, count], index) => (
                    <tr key={index} className={index % 2 === 0 ? "bg-gray-50" : ""}>
                      <td className="py-2 px-4">{value}</td>
                      <td className="py-2 px-4">{count}</td>
                      <td className="py-2 px-4">
                        {totalCount > 0 ? `${((count / totalCount) * 100).toFixed(1)}%` : "0%"}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p className="text-gray-500 italic">No data to display</p>
          )}
        </div>
      </div>
    );
  }

  const root = ReactDOM.createRoot(mount);
  root.render(<ValueCounter />);
}
