      if (!simpleValue2 || !simpleValue2.values) return;

      const valueArray = simpleValue2.values;
      const countMap = {};

      // Count occurrences of each value
      valueArray.forEach(value => {
        countMap[value] = (countMap[value] || 0) + 1;
      });

      // Update the counts, total, and uniqueCount
      setCounts(countMap);
      setTotal(valueArray.length);
      setUniqueCount(Object.keys(countMap).length);
