      setNewValue("");
    };

    // Handle removing a value
    const handleRemoveValue = (valueToRemove) => {
      const updatedValues = simpleValue2.values.filter(value => value !== valueToRemove);
      setSimpleValue2({ ...simpleValue2, values: updatedValues });
    };

    // Handle clearing all values
    const handleClearAll = () => {
      setSimpleValue2({ values: [] });
    };

    return (
      <div className="max-w-4xl mx-auto p-6">
        <h1 className="text-2xl font-bold mb-6 text-center">{title}</h1>

        {/* Input section */}
        <div className="mb-8 bg-white p-4 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-3">Add Values</h2>
          <div className="flex">
            <input
              type="text"
              value={newValue}
              onChange={(e) => setNewValue(e.target.value)}
              className="flex-grow p-2 border rounded-l focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter a value"
            />
            <button
              onClick={handleAddValue}
              className="bg-blue-500 text-white px-4 py-2 rounded-r hover:bg-blue-600 transition"
            >
              Add
            </button>
          </div>

          <div className="mt-4">
            <button
              onClick={handleClearAll}
              className="bg-red-500 text-white px-3 py-1 rounded text-sm hover:bg-red-600 transition"
              disabled={!simpleValue2.values || simpleValue2.values.length === 0}
            >
              Clear All
            </button>
          </div>
        </div>

        {/* Values list */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div className="bg-white p-4 rounded-lg shadow">
            <h2 className="text-lg font-semibold mb-3">Values List</h2>
            {simpleValue2.values && simpleValue2.values.length > 0 ? (
              <ul className="divide-y">
                {simpleValue2.values.map((value, index) => (
                  <li key={index} className="py-2 flex justify-between items-center">
                    <span>{value}</span>
                    <button
                      onClick={() => handleRemoveValue(value)}
                      className="text-red-500 hover:text-red-700"
                    >
                      ❌
                    </button>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500 italic">No values added yet</p>
            )}
          </div>

          {/* Counts display */}
          <div className="bg-white p-4 rounded-lg shadow">
            <h2 className="text-lg font-semibold mb-3">Value Counts</h2>
            {Object.keys(counts).length > 0 ? (
              <ul className="divide-y">
                {Object.entries(counts).map(([value, count], index) => (
                  <li key={index} className="py-2 flex justify-between items-center">
                    <span className="font-medium">{value}</span>
                    <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded-full text-sm">
                      {count}
                    </span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-500 italic">No counts available</p>
            )}
          </div>
        </div>

        {/* Summary statistics */}
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-3">Summary</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="bg-gray-50 p-3 rounded">
              <p className="text-gray-500 text-sm">Total Values</p>
              <p className="text-2xl font-bold">{total}</p>
            </div>
            <div className="bg-gray-50 p-3 rounded">
              <p className="text-gray-500 text-sm">Unique Values</p>
              <p className="text-2xl font-bold">{uniqueCount}</p>
            </div>
            <div className="bg-gray-50 p-3 rounded">
              <p className="text-gray-500 text-sm">Source</p>
              <p className="text-md font-mono">{source}</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Render the component
  const root = ReactDOM.createRoot(mount);
  root.render(<ValueCounter />);
}
