function TodoItem({ todo, onToggle, onDelete }) {
  return (
    <div className="flex items-center p-2 border-b">
      <input
        type="checkbox"
        checked={todo.completed}
        onChange={onToggle}
        className="mr-2"
      />
      <span className={`flex-grow ${todo.completed ? 'line-through text-gray-500' : ''}`}>
        {todo.text}
      </span>
