const title = 'Value Frequency Counter';

function onReady(mount, sourceData, libs) {
  const { d3 } = libs;

  function ValueFrequencyCounter() {
    // Persistent state from schema
    const [simplevalue2, setSimplevalue2] = useDoc('simplevalue2', { values: [] });
    const [source, setSource] = useDoc('source', 'simpleValue2.values');
    const [totalCount, setTotalCount] = useDoc('totalCount', 0);
    const [uniqueCount, setUniqueCount] = useDoc('uniqueCount', 0);
    const [frequencies, setFrequencies] = useDoc('frequencies', []);

    // UI state
    const [isAnalyzing, setIsAnalyzing] = React.useState(false);
    const [showChart, setShowChart] = React.useState(true);
    const [sortBy, setSortBy] = React.useState('count'); // 'count', 'value', or 'percentage'
    const [sortOrder, setSortOrder] = React.useState('desc'); // 'asc' or 'desc'

    // Chart dimensions
    const chartRef = React.useRef(null);
    const chartWidth = 600;
    const chartHeight = 400;
    const margin = { top: 20, right: 30, bottom: 90, left: 60 };

    // Analyze the values
    const analyzeValues = React.useCallback(() => {
      setIsAnalyzing(true);

      // Get the values array
      const values = simplevalue2.values || [];

      // Count frequencies
      const frequencyMap = new Map();
      values.forEach(value => {
        frequencyMap.set(value, (frequencyMap.get(value) || 0) + 1);
      });

      // Convert to array format
      const total = values.length;
      const frequencyArray = Array.from(frequencyMap.entries()).map(([value, count]) => ({
        value,
        count,
        percentage: total > 0 ? (count / total) * 100 : 0
      }));

      // Update state
      setTotalCount(total);
      setUniqueCount(frequencyMap.size);
      setFrequencies(frequencyArray);
      setIsAnalyzing(false);
    }, [simplevalue2, setTotalCount, setUniqueCount, setFrequencies]);

    // Sort frequencies
    const sortedFrequencies = React.useMemo(() => {
      return [...frequencies].sort((a, b) => {
        let comparison = 0;
        if (sortBy === 'count') {
          comparison = a.count - b.count;
        } else if (sortBy === 'value') {
          comparison = a.value.localeCompare(b.value);
        } else if (sortBy === 'percentage') {
          comparison = a.percentage - b.percentage;
        }
        return sortOrder === 'asc' ? comparison : -comparison;
      });
    }, [frequencies, sortBy, sortOrder]);

    // Draw chart using D3
    React.useEffect(() => {
      if (!chartRef.current || !showChart || sortedFrequencies.length === 0) return;

      // Clear previous chart
      d3.select(chartRef.current).selectAll("*").remove();

      const svg = d3.select(chartRef.current)
        .attr("width", chartWidth)
        .attr("height", chartHeight)
        .append("g")
        .attr("transform", `translate(${margin.left},${margin.top})`);

      const width = chartWidth - margin.left - margin.right;
      const height = chartHeight - margin.top - margin.bottom;

      // X scale
      const x = d3.scaleBand()
        .domain(sortedFrequencies.map(d => d.value))
        .range([0, width])
        .padding(0.1);

      // Y scale
      const y = d3.scaleLinear()
        .domain([0, d3.max(sortedFrequencies, d => d.count)])
        .nice()
        .range([height, 0]);

      // X axis
      svg.append("g")
        .attr("transform", `translate(0,${height})`)
        .call(d3.axisBottom(x))
        .selectAll("text")
        .attr("transform", "translate(-10,0)rotate(-45)")
        .style("text-anchor", "end");

      // Y axis
      svg.append("g")
        .call(d3.axisLeft(y));

      // Bars
      svg.selectAll(".bar")
        .data(sortedFrequencies)
        .enter()
        .append("rect")
        .attr("class", "bar")
        .attr("x", d => x(d.value))
        .attr("y", d => y(d.count))
        .attr("width", x.bandwidth())
        .attr("height", d => height - y(d.count))
        .attr("fill", "#4f46e5");

      // X axis label
      svg.append("text")
        .attr("text-anchor", "middle")
        .attr("x", width / 2)
        .attr("y", height + margin.bottom - 10)
        .text("Values");

      // Y axis label
      svg.append("text")
        .attr("text-anchor", "middle")
        .attr("transform", "rotate(-90)")
        .attr("y", -margin.left + 20)
        .attr("x", -height / 2)
        .text("Frequency");

    }, [sortedFrequencies, showChart, d3]);

    // Add sample data for testing
    const addSampleData = () => {
      const sampleData = {
        values: [
          "apple", "banana", "apple", "orange", "banana", 
          "apple", "grape", "orange", "apple", "kiwi",
          "banana", "apple", "orange", "grape", "apple"
        ]
      };
      setSimplevalue2(sampleData);
    };

    // Clear all data
    const clearData = () => {
      setSimplevalue2({ values: [] });
      setTotalCount(0);
      setUniqueCount(0);
      setFrequencies([]);
    };

    // Toggle sort order
    const toggleSort = (field) => {
      if (sortBy === field) {
        setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
      } else {
        setSortBy(field);
        setSortOrder('desc');
      }
    };

    return (
      <div className="container mx-auto p-4 max-w-6xl">
        <h1 className="text-2xl font-bold mb-4">üìä Value Frequency Counter</h1>

        <div className="bg-white rounded-lg shadow p-6 mb-6">
          <h2 className="text-xl font-semibold mb-4">Data Source</h2>
          <div className="flex flex-col md:flex-row gap-4 mb-4">
            <div className="flex-1">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Source Field
              </label>
              <input
                type="text"
                value={source}
                onChange={(e) => setSource(e.target.value)}
                className="w-full p-2 border rounded"
                disabled
              />
            </div>
            <div className="flex items-end space-x-2">
              <button
                onClick={analyzeValues}
                disabled={isAnalyzing || !simplevalue2.values || simplevalue2.values.length === 0}
                className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 disabled:bg-gray-400"
              >
                {isAnalyzing ? 'Analyzing...' : 'Analyze Values'}
              </button>
              <button
                onClick={addSampleData}
                className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
              >
                Add Sample Data
              </button>
              <button
                onClick={clearData}
                className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
              >
                Clear Data
              </button>
            </div>
          </div>

          <div className="mt-4">
            <h3 className="text-lg font-medium mb-2">Current Data</h3>
            <div className="bg-gray-100 p-3 rounded max-h-40 overflow-y-auto">
              {simplevalue2.values && simplevalue2.values.length > 0 ? (
                <div className="flex flex-wrap gap-2">
                  {simplevalue2.values.map((value, index) => (
                    <span key={index} className="bg-indigo-100 text-indigo-800 px-2 py-1 rounded">
                      {value}
                    </span>
                  ))}
                </div>
              ) : (
                <p className="text-gray-500">No data available. Add sample data or input your own values.</p>
              )}
            </div>
          </div>
        </div>

        {totalCount > 0 && (
          <div className="bg-white rounded-lg shadow p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4">Analysis Results</h2>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
              <div className="bg-indigo-50 p-4 rounded-lg">
                <h3 className="text-lg font-medium text-indigo-800">Total Values</h3>
                <p className="text-3xl font-bold">{totalCount}</p>
              </div>
              <div className="bg-purple-50 p-4 rounded-lg">
                <h3 className="text-lg font-medium text-purple-800">Unique Values</h3>
                <p className="text-3xl font-bold">{uniqueCount}</p>
              </div>
              <div className="bg-blue-50 p-4 rounded-lg">
                <h3 className="text-lg font-medium text-blue-800">Most Common</h3>
                <p className="text-3xl font-bold">
                  {sortedFrequencies.length > 0 ? sortedFrequencies[0].value : 'N/A'}
                </p>
              </div>
            </div>

            <div className="mb-4 flex justify-between items-center">
              <h3 className="text-lg font-medium">Frequency Distribution</h3>
              <div className="flex items-center">
                <label className="mr-2 text-sm">Show Chart:</label>
                <input
                  type="checkbox"
                  checked={showChart}
                  onChange={() => setShowChart(!showChart)}
                  className="form-checkbox h-5 w-5 text-indigo-600"
                />
              </div>
            </div>

            {showChart && (
              <div className="mb-6 overflow-x-auto">
                <svg ref={chartRef} width={chartWidth} height={chartHeight}></svg>
              </div>
            )}

            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th 
                      scope="col" 
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer"
                      onClick={() => toggleSort('value')}
                    >
                      Value {sortBy === 'value' && (sortOrder === 'asc' ? '‚Üë' : '‚Üì')}
                    </th>
                    <th 
                      scope="col" 
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer"
                      onClick={() => toggleSort('count')}
                    >
                      Count {sortBy === 'count' && (sortOrder === 'asc' ? '‚Üë' : '‚Üì')}
                    </th>
                    <th 
                      scope="col" 
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer"
                      onClick={() => toggleSort('percentage')}
                    >
                      Percentage {sortBy === 'percentage' && (sortOrder === 'asc' ? '‚Üë' : '‚Üì')}
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {sortedFrequencies.map((item, index) => (
                    <tr key={index} className={index % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        {item.value}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {item.count}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {item.percentage.toFixed(2)}%
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    );
  }

  const root = ReactDOM.createRoot(mount);
  root.render(<ValueFrequencyCounter />);
}
