const title = 'SimpleValue2 Viewer';

function onReady(mount, sourceData) {
  function SimpleValue2Viewer() {
    // Get the values from simplevalue2
    const [simpleValues, setSimpleValues] = useReactiveCell(['simplevalue2', 'values'], []);

    // Get and set the display data
    const [displayValues, setDisplayValues] = useReactiveCell(['displayData', 'values'], []);
    const [count, setCount] = useReactiveCell(['displayData', 'count'], 0);
    const [lastUpdated, setLastUpdated] = useReactiveCell(['displayData', 'lastUpdated'], '');

    // Update display data when simpleValues changes
    React.useEffect(() => {
      if (simpleValues) {
        setDisplayValues([...simpleValues]);
        setCount(simpleValues.length);
        setLastUpdated(new Date().toISOString());
      }
    }, [simpleValues, setDisplayValues, setCount, setLastUpdated]);

    return (
      <div className="max-w-3xl mx-auto p-6 bg-white rounded-lg shadow-md">
        <h1 className="text-2xl font-bold text-gray-800 mb-4">SimpleValue2 Viewer</h1>

        <div className="mb-6 p-4 bg-gray-50 rounded-md">
          <div className="flex justify-between items-center mb-2">
            <h2 className="text-lg font-semibold text-gray-700">Values</h2>
            <span className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm font-medium">
              Count: {count}
            </span>
          </div>

          {count > 0 ? (
            <ul className="divide-y divide-gray-200">
              {displayValues.map((value, index) => (
                <li key={index} className="py-3 px-2 hover:bg-gray-100 transition-colors rounded">
                  <div className="flex items-center">
                    <span className="mr-2 text-gray-500">#{index + 1}</span>
                    <span className="text-gray-800">{value}</span>
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <div className="text-center py-8 text-gray-500">
              <p>No values found in simpleValue2</p>
              <p className="text-sm mt-2">Values will appear here when added</p>
            </div>
          )}
        </div>

        <div className="bg-gray-50 p-4 rounded-md">
          <h3 className="text-md font-medium text-gray-700 mb-2">Metadata</h3>
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div>
              <p className="text-gray-500">Total Values</p>
              <p className="font-medium">{count}</p>
            </div>
            <div>
              <p className="text-gray-500">Last Updated</p>
              <p className="font-medium">
                {lastUpdated ? new Date(lastUpdated).toLocaleString() : 'Never'}
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const root = ReactDOM.createRoot(mount);
  root.render(<SimpleValue2Viewer />);
}
