} from "commontools";

const sendMessage = handler<
  { detail: { message: string } },
  {
    addMessage: Stream<BuiltInLLMMessage>;
  }
>((event, { addMessage }) => {
  addMessage.send({
    role: "user",
    content: [{ type: "text", text: event.detail.message }],
  });
});

const clearChat = handler(
  (
    _: never,
    { messages, pending }: {
      messages: Cell<Array<BuiltInLLMMessage>>;
      pending: Cell<boolean | undefined>;
    },
  ) => {
    messages.set([]);
    pending.set(false);
  },
);

type ChatInput = {
  messages: Default<Array<BuiltInLLMMessage>, []>;
  tools: any;
  theme?: any;
};

type ChatOutput = {
  messages: Array<BuiltInLLMMessage>;
  pending: boolean | undefined;
  addMessage: Stream<BuiltInLLMMessage>;
  cancelGeneration: Stream<void>;
  title?: string;
};

export const TitleGenerator = pattern<
  { model?: string; messages: Array<BuiltInLLMMessage> }
>("Title Generator", ({ model, messages }) => {
  const titleMessages = derive(messages, (m) => {
    if (!m || m.length === 0) return "";

    const messageCount = 2;
    const selectedMessages = m.slice(0, messageCount).filter(Boolean);

    if (selectedMessages.length === 0) return "";

    return selectedMessages.map((msg) => JSON.stringify(msg)).join("\n");
  });

  const { result } = generateObject({
    system:
      "Generate at most a 3-word title based on the following content, respond with NOTHING but the literal title text.",
    prompt: titleMessages,
    model,
    schema: {
      type: "object",
      properties: {
        title: {
          type: "string",
          description: "The title of the chat",
        },
      },
      required: ["title"],
    },
  });

  const title = derive(result, (t) => {
    return t?.title || "Untitled Chat";
  });

  return title;
});

export default pattern<ChatInput, ChatOutput>(
  "Chat",
  ({ messages, tools, theme }) => {
    const model = cell<string>("anthropic:claude-sonnet-4-5");

    const { addMessage, cancelGeneration, pending } = llmDialog({
      system: "You are a helpful assistant with some tools.",
      messages,
      tools,
      model,
    });

    const { result } = fetchData({
      url: "/api/ai/llm/models",
      mode: "json",
    });

    const items = derive(result, (models) => {
      if (!models) return [];
      const items = Object.keys(models as any).map((key) => ({
        label: key,
        value: key,
      }));
      return items;
    });

    const title = TitleGenerator({ model, messages });

    return {
      [NAME]: title,
      [UI]: (
        <ct-screen>
          <ct-hstack justify="between" slot="header">
            <ct-heading level={4}>{title}</ct-heading>
            <ct-tools-chip tools={tools} />
          </ct-hstack>

          <ct-vscroll flex showScrollbar fadeEdges snapToBottom>
            <ct-chat
              theme={theme}
              $messages={messages}
              pending={pending}
              tools={tools}
            />
          </ct-vscroll>

          <div slot="footer">
            <ct-prompt-input
              placeholder="Ask the LLM a question..."
              pending={pending}
              onct-send={sendMessage({ addMessage })}
              onct-stop={cancelGeneration}
            />
            <ct-select
              items={items}
              $value={model}
            />
          </div>
        </ct-screen>
      ),
      messages,
      pending,
      addMessage,
      cancelGeneration,
      title,
    };
  },
);
"},{"name":"/chatbot-outliner.tsx","contents":"/// <cts-enable />
import {
  BuiltInLLMMessage,
  Cell,
  cell,
  Default,
  derive,
  fetchData,
  getRecipeEnvironment,
  h,
  handler,
  ID,
  ifElse,
  JSONSchema,
  lift,
  llm,
  llmDialog,
  NAME,
  navigateTo,
  OpaqueRef,
  pattern,
  str,
  Stream,
  UI,
} from "commontools";

import Chat from "./chatbot.tsx";

type Charm = any;

type OutlinerNode = {
  body: Default<string, "">;
  children: Default<OutlinerNode[], []>;
  attachments: Default<OpaqueRef<any>[], []>;
};

type Outliner = {
  root: OutlinerNode;
};

type PageResult = {
  outline: Default<
    Outliner,
    { root: { body: ""; children: []; attachments: [] } }
  >;
};

export type PageInput = {
  outline: Outliner;
  allCharms: Cell<Charm[]>;
};

const handleCharmLinkClick = handler<
  {
    detail: {
      charm: Cell<Charm>;
    };
  },
  Record<string, never>
>(({ detail }, _) => {
  return navigateTo(detail.charm);
});

export const Page = pattern<PageInput>(
  "Page",
  ({ outline, allCharms }) => {
    return {
      [NAME]: "Page",
      [UI]: (
        <ct-outliner
          $value={outline as any}
          $mentionable={allCharms}
          oncharm-link-click={handleCharmLinkClick({})}
        />
      ),
      outline,
    };
  },
);

type LLMTestInput = {
  title: Default<string, "LLM Test">;
  messages: Default<Array<BuiltInLLMMessage>, []>;
  expandChat: Default<boolean, false>;
  outline: Default<
    Outliner,
    { root: { body: "Untitled Page"; children: []; attachments: [] } }
  >;
  allCharms: Cell<Charm[]>;
};

type LLMTestResult = {
  messages: Default<Array<BuiltInLLMMessage>, []>;
};

// put a node at the end of the outline (by appending to root.children)
const appendOutlinerNode = handler<
  {
    /** The text content/title of the outliner node to be appended */
    body: string;
    /** A cell to store the result message indicating success or error */
    result: Cell<string>;
  },
  { outline: Cell<Outliner> }
>(
  (args, state) => {
    try {
      (state.outline.key("root").key("children")).push({
        body: args.body,
        children: [],
        attachments: [],
      });

      args.result.set(
        `${state.outline.key("root").key("children").get().length} nodes`,
      );
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

export default pattern<LLMTestInput, LLMTestResult>(
  "Outliner",
  ({ title, expandChat, messages, outline, allCharms }) => {
    const tools = {
      appendOutlinerNode: {
        description: "Add a new outliner node.",
        inputSchema: {
          type: "object",
          properties: {
            body: {
              type: "string",
              description: "The title of the new node.",
            },
          },
          required: ["body"],
        } as JSONSchema,
        handler: appendOutlinerNode({ outline }),
      },
    };

    const chat = Chat({ messages, tools });
    const { addMessage, cancelGeneration, pending } = chat;

    return {
      [NAME]: title,
      [UI]: (
        <ct-screen>
          <ct-hstack justify="between" slot="header">
            <div></div>
            <div>
              <ct-checkbox $checked={expandChat}>Show Chat</ct-checkbox>
            </div>
          </ct-hstack>

          <ct-autolayout tabNames={["Chat", "Tools"]}>
            <ct-screen>
              <div slot="header">
                <ct-input
                  $value={title}
                  placeholder="Enter title..."
                />
              </div>

              <ct-vscroll flex showScrollbar fadeEdges snapToBottom>
                <ct-vstack data-label="Tools">
                  <Page outline={outline} allCharms={allCharms} />
                </ct-vstack>
              </ct-vscroll>
            </ct-screen>

            {ifElse(
              expandChat,
              chat,
              null,
            )}
          </ct-autolayout>
        </ct-screen>
      ),
      messages,
    };
  },
);
"},{"name":"/chatbot-note-composed.tsx","contents":"/// <cts-enable />
import {
  BuiltInLLMMessage,
  Cell,
  cell,
  Default,
  derive,
  fetchData,
  getRecipeEnvironment,
  h,
  handler,
  ID,
  ifElse,
  JSONSchema,
  lift,
  llm,
  llmDialog,
  NAME,
  navigateTo,
  OpaqueRef,
  pattern,
  str,
  Stream,
  toSchema,
  UI,
} from "commontools";

import Chat from "./chatbot.tsx";
import Note from "./note.tsx";
import Tools, {
  addListItem,
  calculator,
  ListItem,
  readListItems,
  readWebpage,
  searchWeb,
} from "./common-tools.tsx";

export type MentionableCharm = {
  [NAME]: string;
  content?: string;
  mentioned?: MentionableCharm[];
};

// export type ChatbotNoteInput = {
//   content: Default<string, "">;
//   allCharms?: Cell<MentionableCharm[]>;
// };

const handleCharmLinkClick = handler<
  {
    detail: {
      charm: Cell<MentionableCharm>;
    };
  },
  Record<string, never>
>(({ detail }, _) => {
  return navigateTo(detail.charm);
});

const handleCharmLinkClicked = handler(
  (_: any, { charm }: { charm: Cell<MentionableCharm> }) => {
    return navigateTo(charm);
  },
);

type ChatbotNoteInput = {
  title: Default<string, "LLM Test">;
  messages: Default<Array<BuiltInLLMMessage>, []>;
  content: Default<string, "">;
  allCharms: Cell<MentionableCharm[]>;
};

type ChatbotNoteResult = {
  messages: Default<Array<BuiltInLLMMessage>, []>;
  mentioned: Default<Array<MentionableCharm>, []>;
  backlinks: Default<Array<MentionableCharm>, []>;
  content: Default<string, "">;
  note: any;
  chat: any;
  list: Default<ListItem[], []>;
};

const newNote = handler<
  {
    /** The text content of the note */
    title: string;
    content?: string;
    /** A cell to store the result message indicating success or error */
    result: Cell<string>;
  },
  { allCharms: Cell<MentionableCharm[]> }
>(
  (args, state) => {
    try {
      const n = Note({
        title: args.title,
        content: args.content || "",
        allCharms: state.allCharms,
      });

      args.result.set(
        `Created note ${args.title}!`,
      );

      state.allCharms.push(n as unknown as MentionableCharm);
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

// put a note at the end of the outline (by appending to root.children)
const editNote = handler<
  {
    /** The text content of the note */
    body: string;
    /** A cell to store the result message indicating success or error */
    result: Cell<string>;
  },
  { content: Cell<string> }
>(
  (args, state) => {
    try {
      state.content.set(args.body);

      args.result.set(
        `Updated note!`,
      );
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

const readNote = handler<
  {
    /** A cell to store the result text */
    result: Cell<string>;
  },
  { content: string }
>(
  (args, state) => {
    try {
      args.result.set(state.content);
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

const listMentionable = handler<
  {
    /** A cell to store the result text */
    result: Cell<string>;
  },
  { allCharms: { [NAME]: string }[] }
>(
  (args, state) => {
    try {
      const namesList = state.allCharms.map((charm) => charm[NAME]);
      args.result.set(JSON.stringify(namesList));
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

const readNoteByIndex = handler<
  {
    /** A cell to store the result text */
    index: number;
    result: Cell<string>;
  },
  { allCharms: { [NAME]: string; content?: string }[] }
>(
  (args, state) => {
    try {
      args.result.set(
        state.allCharms[args.index]?.content || "No content found",
      );
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

const editNoteByIndex = handler<
  {
    /** The index of the note to edit */
    index: number;
    /** The new text content of the note */
    body: string;
    /** A cell to store the result message indicating success or error */
    result: Cell<string>;
  },
  { allCharms: Cell<MentionableCharm[]> }
>(
  (args, state) => {
    try {
      const charms = state.allCharms.get();
      if (args.index < 0 || args.index >= charms.length) {
        args.result.set(`Error: Invalid index ${args.index}`);
        return;
      }

      state.allCharms.key(args.index).key("content").set(args.body);
      args.result.set(`Updated note at index ${args.index}!`);
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

const navigateToNote = handler<
  {
    /** The index of the note to navigate to */
    index: number;
    /** A cell to store the result message indicating success or error */
    result: Cell<string>;
  },
  { allCharms: Cell<MentionableCharm[]> }
>(
  (args, state) => {
    try {
      const charms = state.allCharms.get();
      if (args.index < 0 || args.index >= charms.length) {
        args.result.set(`Error: Invalid index ${args.index}`);
        return;
      }

      const targetCharm = charms[args.index];
      args.result.set(`Navigating to note: ${targetCharm[NAME]}`);

      return navigateTo(state.allCharms.key(args.index));
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

export default pattern<ChatbotNoteInput, ChatbotNoteResult>(
  "Chatbot + Note",
  ({ title, messages, content, allCharms }) => {
    const list = cell<ListItem[]>([]);

    const tools = {
      searchWeb: {
        pattern: searchWeb,
      },
      readWebpage: {
        pattern: readWebpage,
      },
      calculator: {
        pattern: calculator,
      },
      addListItem: {
        handler: addListItem({ list }),
      },
      readListItems: {
        handler: readListItems({ list }),
      },
      editActiveNote: {
        description: "Modify the shared note.",
        inputSchema: {
          type: "object",
          properties: {
            body: {
              type: "string",
              description: "The content of the note.",
            },
          },
          required: ["body"],
        } as JSONSchema,
        handler: editNote({ content }),
      },
      readActiveNote: {
        description: "Read the currently focused note.",
        inputSchema: {
          type: "object",
          properties: {},
          required: [],
        } as JSONSchema,
        handler: readNote({ content }),
      },
      listNotes: {
        description:
          "List all mentionable note titles (read the body with readNoteByIndex).",
        inputSchema: {
          type: "object",
          properties: {},
          required: [],
        } as JSONSchema,
        handler: listMentionable({
          allCharms: allCharms as unknown as OpaqueRef<MentionableCharm[]>,
        }),
      },
      readNoteByIndex: {
        description:
          "Read the body of a note by its index in the listNotes() list.",
        inputSchema: {
          type: "object",
          properties: {
            index: {
              type: "number",
              description: "The index of the note in the notes list.",
            },
          },
          required: ["index"],
        } as JSONSchema,
        handler: readNoteByIndex({
          allCharms: allCharms as unknown as OpaqueRef<MentionableCharm[]>,
        }),
      },
      editNoteByIndex: {
        description:
          "Edit the body of a note by its index in the listNotes() list.",
        inputSchema: {
          type: "object",
          properties: {
            index: {
              type: "number",
              description: "The index of the note in the notes list.",
            },
            body: {
              type: "string",
              description: "The new content of the note.",
            },
          },
          required: ["index", "body"],
        } as JSONSchema,
        handler: editNoteByIndex({
          allCharms: allCharms as unknown as OpaqueRef<MentionableCharm[]>,
        }),
      },
      navigateToNote: {
        description: "Navigate to a note by its index in the listNotes() list.",
        inputSchema: {
          type: "object",
          properties: {
            index: {
              type: "number",
              description: "The index of the note in the notes list.",
            },
          },
          required: ["index"],
        } as JSONSchema,
        handler: navigateToNote({
          allCharms: allCharms as unknown as OpaqueRef<MentionableCharm[]>,
        }),
      },
      newNote: {
        description: "Read the shared note.",
        inputSchema: {
          type: "object",
          properties: {
            title: {
              type: "string",
              description: "The title of the note.",
            },
            content: {
              type: "string",
              description: "The content of the note.",
            },
          },
          required: ["title"],
        } as JSONSchema,
        handler: newNote({
          allCharms: allCharms as unknown as OpaqueRef<MentionableCharm[]>,
        }),
      },
    };

    const chat = Chat({ messages, tools });
    const note = Note({ title, content, allCharms });

    return {
      [NAME]: title,
      chat,
      note,
      content,
      messages,
      mentioned: note.mentioned,
      backlinks: note.backlinks,
      list,
    };
  },
);
"},{"name":"/note.tsx","contents":"/// <cts-enable />
import {
  Cell,
  cell,
  Default,
  derive,
  h,
  handler,
  lift,
  NAME,
  navigateTo,
  OpaqueRef,
  pattern,
  toSchema,
  UI,
} from "commontools";

export type MentionableCharm = {
  [NAME]: string;
  content?: string;
  mentioned?: MentionableCharm[];
};

type Input = {
  title: Default<string, "Untitled Note">;
  content: Default<string, "">;
  allCharms: Cell<MentionableCharm[]>;
};

type Output = {
  mentioned: Default<Array<MentionableCharm>, []>;
  content: Default<string, "">;
  backlinks: Default<Array<MentionableCharm>, []>;
};

const updateTitle = handler<
  { detail: { value: string } },
  { title: Cell<string> }
>(
  (event, state) => {
    state.title.set(event.detail?.value ?? "");
  },
);

const updateContent = handler<
  { detail: { value: string } },
  { content: Cell<string> }
>(
  (event, state) => {
    state.content.set(event.detail?.value ?? "");
  },
);

const handleCharmLinkClick = handler<
  {
    detail: {
      charm: Cell<MentionableCharm>;
    };
  },
  Record<string, never>
>(({ detail }, _) => {
  return navigateTo(detail.charm);
});

const handleNewBacklink = handler<
  {
    detail: {
      text: string;
      charmId: any;
      charm: Cell<MentionableCharm>;
      navigate: boolean;
    };
  },
  {
    allCharms: Cell<MentionableCharm[]>;
  }
>(({ detail }, { allCharms }) => {
  console.log("new charm", detail.text, detail.charmId);

  if (detail.navigate) {
    return navigateTo(detail.charm);
  } else {
    allCharms.push(detail.charm as unknown as MentionableCharm);
  }
});

const handleCharmLinkClicked = handler(
  (_: any, { charm }: { charm: Cell<MentionableCharm> }) => {
    return navigateTo(charm);
  },
);

const Note = pattern<Input, Output>(
  "Note",
  ({ title, content, allCharms }) => {
    const mentioned = cell<MentionableCharm[]>([]);

    const computeBacklinks = lift<
      { allCharms: Cell<MentionableCharm[]>; content: Cell<string> },
      MentionableCharm[]
    >(
      ({ allCharms, content }) => {
        const cs = allCharms.get();
        if (!cs) return [];

        const self = cs.find((c) => c.content === content.get());

        const results = self
          ? cs.filter((c) =>
            c.mentioned?.some((m) => m.content === self.content) ?? false
          )
          : [];

        return results;
      },
    );

    const backlinks: OpaqueRef<MentionableCharm[]> = computeBacklinks({
      allCharms,
      content: content as unknown as Cell<string>, // TODO(bf): this is valid, but types complain
    });

    // The only way to serialize a pattern, apparently?
    const pattern = derive(undefined, () => JSON.stringify(Note));

    return {
      [NAME]: title,
      [UI]: (
        <ct-screen>
          <div slot="header">
            <ct-input
              $value={title}
              placeholder="Enter title..."
            />
          </div>

          <ct-code-editor
            $value={content}
            $mentionable={allCharms}
            $mentioned={mentioned}
            $pattern={pattern}
            onbacklink-click={handleCharmLinkClick({})}
            onbacklink-create={handleNewBacklink({
              allCharms: allCharms as unknown as MentionableCharm[],
            })}
            language="text/markdown"
            theme="light"
            wordWrap
            tabIndent
            lineNumbers
          />
        </ct-screen>
      ),
      title,
      content,
      mentioned,
      backlinks,
    };
  },
);

export default Note;
"},{"name":"/chatbot-list-view.tsx","contents":"/// <cts-enable />
import {
  Cell,
  cell,
  Default,
  derive,
  h,
  handler,
  ID,
  ifElse,
  lift,
  NAME,
  navigateTo,
  OpaqueRef,
  pattern,
  toSchema,
  UI,
} from "commontools";

import Chat from "./chatbot-note-composed.tsx";
import { ListItem } from "./common-tools.tsx";

export type MentionableCharm = {
  [NAME]: string;
  content?: string;
  mentioned?: MentionableCharm[];
};

type CharmEntry = {
  [ID]: string; // randomId is a string
  local_id: string; // same as ID but easier to access
  charm: any;
};

type Input = {
  selectedCharm: Default<{ charm: any }, { charm: undefined }>;
  charmsList: Default<CharmEntry[], []>;
  allCharms: Cell<any[]>;
  theme?: {
    accentColor: Default<string, "#3b82f6">;
    fontFace: Default<string, "system-ui, -apple-system, sans-serif">;
    borderRadius: Default<string, "0.5rem">;
  };
};

type Output = {
  selectedCharm: Default<{ charm: any }, { charm: undefined }>;
};

const removeChat = handler<
  unknown,
  {
    charmsList: Cell<CharmEntry[]>;
    id: string;
    selectedCharm: Cell<Default<{ charm: any }, { charm: undefined }>>;
  }
>(
  (
    _,
    { charmsList, id, selectedCharm },
  ) => {
    const list = charmsList.get();
    const index = list.findIndex((entry) => entry.local_id === id);
    if (index === -1) return;

    const removed = list[index];
    const next = [...list];
    next.splice(index, 1);
    charmsList.set(next);

    // If we removed the currently selected charm, choose a new selection.
    const current = selectedCharm.get();
    if (current?.charm === removed.charm) {
      const replacement = next[index] ?? next[index - 1];
      if (replacement) {
        selectedCharm.set({ charm: replacement.charm });
      } else {
        selectedCharm.set({ charm: undefined as unknown as any });
      }
    }
  },
);

// this will be called whenever charm or selectedCharm changes
// pass isInitialized to make sure we dont call this each time
// we change selectedCharm, otherwise creates a loop
const storeCharm = lift(
  toSchema<{
    charm: any;
    selectedCharm: Cell<Default<{ charm: any }, { charm: undefined }>>;
    charmsList: Cell<CharmEntry[]>;
    allCharms: Cell<any[]>;
    theme?: {
      accentColor: Default<string, "#3b82f6">;
      fontFace: Default<string, "system-ui, -apple-system, sans-serif">;
      borderRadius: Default<string, "0.5rem">;
    };
    isInitialized: Cell<boolean>;
  }>(),
  undefined,
  ({ charm, selectedCharm, charmsList, isInitialized, allCharms }) => { // Not including `allCharms` is a compile error...
    if (!isInitialized.get()) {
      console.log(
        "storeCharm storing charm:",
        charm,
      );
      selectedCharm.set({ charm });

      // create the chat charm with a custom name including a random suffix
      const randomId = Math.random().toString(36).substring(2, 10); // Random 8-char string
      charmsList.push({ [ID]: randomId, local_id: randomId, charm });

      isInitialized.set(true);
      return charm;
    } else {
      console.log("storeCharm: already initialized");
    }
    return undefined;
  },
);

const populateChatList = lift(
  toSchema<{
    charmsList: CharmEntry[];
    allCharms: Cell<any[]>;
    selectedCharm: Cell<{ charm: any }>;
  }>(),
  undefined,
  (
    { charmsList, allCharms, selectedCharm },
  ) => {
    if (charmsList.length === 0) {
      const isInitialized = cell(false);
      return storeCharm({
        charm: Chat({
          title: "New Chat",
          messages: [],
          content: "",
          allCharms,
        }),
        selectedCharm,
        charmsList,
        allCharms,
        isInitialized: isInitialized as unknown as Cell<boolean>,
      });
    }

    return charmsList;
  },
);

const createChatRecipe = handler<
  unknown,
  {
    selectedCharm: Cell<{ charm: any }>;
    charmsList: Cell<CharmEntry[]>;
    allCharms: Cell<any[]>;
  }
>(
  (_, { selectedCharm, charmsList, allCharms }) => {
    const isInitialized = cell(false);

    const charm = Chat({
      title: "New Chat",
      messages: [],
      content: "",
      allCharms,
    });
    // store the charm ref in a cell (pass isInitialized to prevent recursive calls)
    return storeCharm({
      charm,
      selectedCharm,
      charmsList: charmsList as unknown as OpaqueRef<CharmEntry[]>,
      allCharms,
      isInitialized: isInitialized as unknown as Cell<boolean>,
    });
  },
);

const selectCharm = handler<
  unknown,
  { selectedCharm: Cell<{ charm: any }>; charm: any }
>(
  (_, { selectedCharm, charm }) => {
    console.log("selectCharm: updating selectedCharm to ", charm);
    selectedCharm.set({ charm });
    return selectedCharm;
  },
);

const logCharmsList = lift<
  { charmsList: Cell<CharmEntry[]> },
  Cell<CharmEntry[]>
>(
  ({ charmsList }) => {
    console.log("logCharmsList: ", charmsList.get());
    return charmsList;
  },
);

const handleCharmLinkClicked = handler(
  (_: any, { charm }: { charm: Cell<MentionableCharm> }) => {
    return navigateTo(charm);
  },
);

const combineLists = lift(
  (
    { allCharms, charmsList }: { allCharms: any[]; charmsList: CharmEntry[] },
  ) => {
    return [...charmsList.map((c) => c.charm), ...allCharms];
  },
);

const getSelectedCharm = lift<
  { entry: { charm: any | undefined } },
  {
    chat: unknown;
    note: unknown;
    list: ListItem[];
    backlinks: MentionableCharm[];
    mentioned: MentionableCharm[];
  } | undefined
>(
  ({ entry }) => {
    return entry?.charm;
  },
);

const getCharmName = lift(({ charm }: { charm: any }) => {
  return charm?.[NAME] || "Unknown";
});

// create the named cell inside the pattern body, so we do it just once
export default pattern<Input, Output>(
  "Launcher",
  ({ selectedCharm, charmsList, allCharms, theme }) => {
    logCharmsList({ charmsList: charmsList as unknown as Cell<CharmEntry[]> });

    populateChatList({
      selectedCharm: selectedCharm as unknown as Cell<
        Pick<CharmEntry, "charm">
      >,
      charmsList,
      allCharms,
    });

    const combined = combineLists({
      allCharms: allCharms as unknown as any[],
      charmsList,
    });

    const selected = getSelectedCharm({ entry: selectedCharm });

    const localTheme = theme ?? {
      accentColor: cell("#3b82f6"),
      fontFace: cell("system-ui, -apple-system, sans-serif"),
      borderRadius: cell("0.5rem"),
    };

    return {
      [NAME]: "Launcher",
      [UI]: (
        <ct-theme theme={localTheme as any}>
          <ct-screen>
            <div slot="header">
              <ct-toolbar dense sticky>
                <div slot="start">
                  <ct-button
                    id="new-chat-btn"
                    onClick={createChatRecipe({
                      selectedCharm,
                      charmsList,
                      allCharms: combined as unknown as any,
                    })}
                  >
                    Create New Chat
                    <ct-kbd>alt+N</ct-kbd>
                  </ct-button>
                </div>
              </ct-toolbar>

              {/* Keyboard shortcuts */}
              <ct-keybind
                code="KeyN"
                alt
                preventDefault
                onct-keybind={createChatRecipe({
                  selectedCharm,
                  charmsList,
                  allCharms: combined as unknown as any,
                })}
              />
            </div>
            <ct-autolayout
              leftOpen
              rightOpen={false}
              tabNames={["Chat", "Note"]}
            >
              {/* workaround: this seems to correctly start the sub-recipes on a refresh while directly rendering does not */}
              {/* this should be fixed after the builder-refactor (DX1) */}
              <ct-screen>
                <ct-render $cell={selected.chat} />
              </ct-screen>
              <ct-screen>
                <ct-render $cell={selected.note} />
              </ct-screen>

              <aside slot="left">
                <div>
                  <ct-heading level={3}>Chat List</ct-heading>
                </div>
                <div role="list">
                  {charmsList.map((charmEntry) => (
                    <ct-list-item
                      onct-activate={selectCharm({
                        selectedCharm,
                        charm: charmEntry.charm,
                      })}
                    >
                      <span>{getCharmName({ charm: charmEntry.charm })}</span>
                      <span slot="meta">{charmEntry.local_id}</span>
                      <ct-button
                        slot="actions"
                        size="sm"
                        title="Delete Chat"
                        variant="destructive"
                        onClick={removeChat({
                          charmsList: charmsList as unknown as OpaqueRef<
                            CharmEntry[]
                          >,
                          id: charmEntry.local_id,
                          selectedCharm: selectedCharm as unknown as OpaqueRef<
                            Default<{ charm: any }, { charm: undefined }>
                          >,
                        })}
                      >
                        üóëÔ∏è
                      </ct-button>
                    </ct-list-item>
                  ))}
                </div>
              </aside>

              <aside slot="right">
                {ifElse(
                  selected,
                  <>
                    <div>
                      <ct-heading level={4}>Backlinks</ct-heading>
                      <ct-vstack>
                        {selected?.backlinks?.map((
                          charm: MentionableCharm,
                        ) => (
                          <ct-button
                            onClick={handleCharmLinkClicked({ charm })}
                          >
                            {charm?.[NAME]}
                          </ct-button>
                        ))}
                      </ct-vstack>
                    </div>
                    <ct-ct-collapsible>
                      <ct-heading slot="trigger" level={5} no-margin>
                        List
                      </ct-heading>
                      <ct-list $value={selected.list} />
                    </ct-ct-collapsible>
                    <ct-collapsible>
                      <ct-heading slot="trigger" level={5} no-margin>
                        Mentioned Charms
                      </ct-heading>
                      <ct-vstack>
                        {selected?.mentioned?.map((
                          charm: MentionableCharm,
                        ) => (
                          charm
                            ? (
                              <ct-button
                                onClick={handleCharmLinkClicked({ charm })}
                              >
                                {charm[NAME]}
                              </ct-button>
                            )
                            : null
                        ))}
                      </ct-vstack>
                    </ct-collapsible>
                  </>,
                  null,
                )}
                <ct-collapsible>
                  <ct-heading slot="trigger" level={5} no-margin>
                    Theme
                  </ct-heading>
                  <ct-vstack style="padding: 0.5rem 0; gap: 0.5rem;">
                    <ct-vstack>
                      <ct-text>Font Family</ct-text>
                      <ct-select
                        items={[
                          {
                            label: "System",
                            value: "system-ui, -apple-system, sans-serif",
                          },
                          {
                            label: "Monospace",
                            value: "ui-monospace, Consolas, monospace",
                          },
                          {
                            label: "Serif",
                            value: "Georgia, Times, serif",
                          },
                          {
                            label: "Sans Serif",
                            value: "Arial, Helvetica, sans-serif",
                          },
                        ]}
                        $value={localTheme.fontFace}
                      />
                    </ct-vstack>

                    <ct-vstack>
                      <ct-text>Accent Color</ct-text>
                      <ct-select
                        items={[
                          { label: "Blue", value: "#3b82f6" },
                          { label: "Purple", value: "#8b5cf6" },
                          { label: "Green", value: "#10b981" },
                          { label: "Red", value: "#ef4444" },
                          { label: "Orange", value: "#f97316" },
                          { label: "Pink", value: "#ec4899" },
                          { label: "Indigo", value: "#6366f1" },
                          { label: "Teal", value: "#14b8a6" },
                        ]}
                        $value={localTheme.accentColor}
                      />
                    </ct-vstack>

                    <ct-vstack>
                      <ct-text>Border Radius</ct-text>
                      <ct-select
                        items={[
                          { label: "None", value: "0px" },
                          { label: "Small", value: "0.25rem" },
                          { label: "Medium", value: "0.5rem" },
                          { label: "Large", value: "0.75rem" },
                          { label: "Extra Large", value: "1rem" },
                          { label: "Rounded", value: "1.5rem" },
                        ]}
                        $value={localTheme.borderRadius}
                      />
                    </ct-vstack>
                  </ct-vstack>
                </ct-collapsible>
              </aside>
            </ct-autolayout>
          </ct-screen>
        </ct-theme>
      ),
      selectedCharm,
      charmsList,
    };
  },
);
"},{"name":"/common-tools.tsx","contents":"/// <cts-enable />
import {
  BuiltInLLMMessage,
  BuiltInLLMTool,
  Cell,
  cell,
  Default,
  derive,
  fetchData,
  h,
  handler,
  ifElse,
  llmDialog,
  NAME,
  pattern,
  Stream,
  UI,
} from "commontools";

///// COMMON TOOLS (get it?) ////

/**
 * Calculate the result of a mathematical expression.
 * Supports +, -, *, /, and parentheses.
 */
type CalculatorRequest = {
  /** The mathematical expression to evaluate. */
  expression: string;
};

export const calculator = pattern<
  CalculatorRequest,
  string | { error: string }
>("Calculator", ({ expression }) => {
  return derive(expression, (expr) => {
    const sanitized = expr.replace(/[^0-9+\-*/().\s]/g, "");
    let result;
    try {
      result = Function(`"use strict"; return (${sanitized})`)();
    } catch (error) {
      result = { error: (error as any)?.message || "<error>" };
    }
    return result;
  });
});

/** Add an item to the list. */
type AddListItemRequest = {
  /** The item to add to the list. */
  item: string;
  result: Cell<string>;
};

/** Read all items from the list. */
type ReadListItemsRequest = {
  result: Cell<string>;
};

export type ListItem = {
  title: string;
};

export const addListItem = handler<
  AddListItemRequest,
  { list: Cell<ListItem[]> }
>(
  (args, state) => {
    try {
      state.list.push({ title: args.item });
      args.result.set(`${state.list.get().length} items`);
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

export const readListItems = handler<
  ReadListItemsRequest,
  { list: ListItem[] }
>(
  (args, state) => {
    try {
      const items = state.list;
      if (items.length === 0) {
        args.result.set("The list is empty");
      } else {
        const itemList = items.map((item, index) =>
          `${index + 1}. ${item.title}`
        ).join("\n");
        args.result.set(`List items (${items.length} total):\n${itemList}`);
      }
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

/** Search the web for information. */
type SearchQuery = {
  /** The query to search the web for. */
  query: string;
};

type SearchWebResult = {
  results: {
    title: string;
    url: string;
    description: string;
  }[];
};

export const searchWeb = pattern<
  SearchQuery,
  SearchWebResult | { error: string }
>("Search Web", ({ query }) => {
  const { result, error } = fetchData<SearchWebResult>({
    url: "/api/agent-tools/web-search",
    mode: "json",
    options: {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: {
        query,
        max_results: 5,
      },
    },
  });

  // TODO(seefeld): Should we instead return { result, error }? Or allocate a
  // special [ERROR] for errors? Ideally this isn't specific to using recipes as
  // tools but a general pattern.
  return ifElse(error, { error }, result);
});

/** Read and extract content from a specific webpage URL. */
type ReadWebRequest = {
  /** The URL of the webpage to read and extract content from. */
  url: string;
};

type ReadWebResult = {
  content: string;
  metadata: {
    title?: string;
    author?: string;
    date?: string;
    word_count: number;
  };
};

export const readWebpage = pattern<
  ReadWebRequest,
  ReadWebResult | { error: string }
>("Read Webpage", ({ url }) => {
  const { result, error } = fetchData<ReadWebResult>({
    url: "/api/agent-tools/web-read",
    mode: "json",
    options: {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: {
        url,
        max_tokens: 4000,
        include_code: true,
      },
    },
  });

  return ifElse(error, { error }, result);
});

type ToolsInput = {
  list: ListItem[];
};

export default pattern<ToolsInput>("Tools", ({ list }) => {
  const tools: Record<string, BuiltInLLMTool> = {
    search_web: {
      pattern: searchWeb,
    },
    read_webpage: {
      pattern: readWebpage,
    },
    calculator: {
      pattern: calculator,
    },
    addListItem: {
      handler: addListItem({ list }),
    },
  };

  return { tools, list };
});
"}]}}

