import {
  Cell,
  derive,
  handler,
  NAME,
  navigateTo,
  recipe,
  str,
  UI,
  wish,
} from "commontools";

import Chatbot from "./chatbot.tsx";
import ChatbotOutliner from "./chatbot-outliner.tsx";
import { default as Note } from "./note.tsx";
import BacklinksIndex, { type MentionableCharm } from "./backlinks-index.tsx";
import ChatList from "./chatbot-list-view.tsx";
import OmniboxFAB from "./omnibox-fab.tsx";

type MinimalCharm = {
  [NAME]?: string;
};

type CharmsListInput = void;

// Recipe returns only UI, no data outputs (only symbol properties)
interface CharmsListOutput {
  [key: string]: unknown;
  backlinksIndex: {
    mentionable: MentionableCharm[];
  };
  sidebarUI: unknown;
  fabUI: unknown;
}

const visit = handler<
  Record<string, never>,
  { charm: Cell<MinimalCharm> }
>((_, state) => {
  return navigateTo(state.charm);
}, { proxy: true });

const removeCharm = handler<
  Record<string, never>,
  {
    charm: Cell<MinimalCharm>;
    allCharms: Cell<MinimalCharm[]>;
  }
>((_, state) => {
  const allCharmsValue = state.allCharms.get();
  const index = allCharmsValue.findIndex((c: any) => state.charm.equals(c));

  if (index !== -1) {
    const charmListCopy = [...allCharmsValue];
    console.log("charmListCopy before", charmListCopy.length);
    charmListCopy.splice(index, 1);
    console.log("charmListCopy after", charmListCopy.length);
    state.allCharms.set(charmListCopy);
  }
});

const toggleFab = handler<any, { fabExpanded: Cell<boolean> }>(
  (_, { fabExpanded }) => {
    fabExpanded.set(!fabExpanded.get());
  },
);

const spawnChatList = handler<void, void>((_, __) => {
  return navigateTo(ChatList({
    selectedCharm: { charm: undefined },
    charmsList: [],
  }));
});

const spawnChatbot = handler<void, void>((_, __) => {
  return navigateTo(Chatbot({
    messages: [],
    tools: undefined,
  }));
});

const spawnChatbotOutliner = handler<void, void>((_, __) => {
  return navigateTo(ChatbotOutliner({
    title: "Chatbot Outliner",
    expandChat: false,
    messages: [],
    outline: {
      root: { body: "", children: [], attachments: [] },
    },
  }));
});

const spawnNote = handler<void, void>((_, __) => {
  return navigateTo(Note({
    title: "New Note",
    content: "",
  }));
});

export default recipe<CharmsListInput, CharmsListOutput>(
  "DefaultCharmList",
  (_) => {
    const allCharms = derive<MentionableCharm[], MentionableCharm[]>(
      wish<MentionableCharm[]>("#allCharms"),
      (c) => c,
    );
    const index = BacklinksIndex({ allCharms });

    const fab = OmniboxFAB({
      mentionable: index.mentionable as unknown as Cell<MentionableCharm[]>,
    });

    return {
      backlinksIndex: index,
      [NAME]: str`DefaultCharmList (${allCharms.length})`,
      [UI]: (
        <ct-screen>
          <ct-keybind
            code="KeyN"
            alt
            preventDefault
            onct-keybind={spawnChatList()}
          />
          <ct-keybind
            code="KeyO"
            meta
            preventDefault
            onct-keybind={toggleFab({ fabExpanded: fab.fabExpanded })}
          />
          <ct-keybind
            code="KeyO"
            ctrl
            preventDefault
            onct-keybind={toggleFab({ fabExpanded: fab.fabExpanded })}
          />

          <ct-toolbar slot="header" sticky>
            <div slot="start">
              <ct-button
                onClick={spawnChatList()}
              >
                üìÇ Chat List
              </ct-button>
              <ct-button
                onClick={spawnChatbot()}
              >
                üí¨ Chatbot
              </ct-button>
              <ct-button
                onClick={spawnChatbotOutliner()}
              >
                üìù Chatbot Outliner
              </ct-button>
              <ct-button
                onClick={spawnNote()}
              >
                üìÑ Note
              </ct-button>
            </div>
          </ct-toolbar>

          <ct-vscroll flex showScrollbar>
            <ct-vstack gap="4" padding="6">
              <h2>Charms ({allCharms.length})</h2>

              <ct-table full-width hover>
                <thead>
                  <tr>
                    <th>Charm Name</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {allCharms.map((charm) => (
                    <tr>
                      <td>{charm?.[NAME] || "Untitled Charm"}</td>
                      <td>
                        <ct-hstack gap="2">
                          <ct-button
                            size="sm"
                            onClick={visit({ charm })}
                          >
                            Visit
                          </ct-button>
                          <ct-button
                            size="sm"
                            variant="destructive"
                            onClick={removeCharm({ charm, allCharms })}
                          >
                            Remove
                          </ct-button>
                        </ct-hstack>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </ct-table>
            </ct-vstack>
          </ct-vscroll>
        </ct-screen>
      ),
      sidebarUI: undefined,
      fabUI: fab[UI],
    };
  },
);
"},{"name":"/chatbot.tsx","contents":"/// <cts-enable />
import {
  BuiltInLLMMessage,
  Cell,
  cell,
  Default,
  derive,
  fetchData,
  generateObject,
  handler,
  llmDialog,
  NAME,
  navigateTo,
  recipe,
  Stream,
  UI,
  VNode,
  wish,
} from "commontools";
import { type MentionableCharm } from "./backlinks-index.tsx";

function schemaifyWish<T>(path: string) {
  return derive<T, T>(wish<T>(path), (i) => i);
}

const addAttachment = handler<
  {
    detail: {
      attachment: PromptAttachment;
    };
  },
  {
    allAttachments: Cell<Array<PromptAttachment>>;
  }
>((event, { allAttachments }) => {
  const { attachment } = event.detail;
  const current = allAttachments.get() || [];
  allAttachments.set([...current, attachment]);
});

const removeAttachment = handler<
  {
    detail: {
      id: string;
    };
  },
  {
    allAttachments: Cell<Array<PromptAttachment>>;
  }
>((event, { allAttachments }) => {
  const { id } = event.detail;
  const current = allAttachments.get() || [];
  allAttachments.set(current.filter((a) => a.id !== id));
});

const sendMessage = handler<
  {
    detail: {
      text: string;
      attachments: Array<PromptAttachment>;
      mentions: Array<any>;
      message: string; // Backward compatibility
    };
  },
  {
    addMessage: Stream<BuiltInLLMMessage>;
    allAttachments: Cell<Array<PromptAttachment>>;
  }
>((event, { addMessage, allAttachments }) => {
  const { text } = event.detail;

  // Build content array from text and attachments
  const contentParts = [{ type: "text" as const, text }];

  // Get current attachments from the global list
  const attachments = allAttachments.get() || [];

  // Compute mentions from mention attachments so they are available to consumers
  const _mentions = attachments
    .filter((a) => a.type === "mention" && a.charm)
    .map((a) => a.charm);

  // Process attachments
  for (const attachment of attachments) {
    if (attachment.type === "file" && attachment.data) {
      // For now, add a text reference
      contentParts.push({
        type: "text" as const,
        text: `[Attached file: ${attachment.name}]`,
      });
    } else if (attachment.type === "clipboard" && attachment.data) {
      // Append clipboard content as additional context
      contentParts.push({
        type: "text" as const,
        text: `\n\n--- Pasted content ---\n${attachment.data}`,
      });
    }
    // Note: mentions are already in the text as clean names
    // The charm references are available in attachment.charm if needed
  }

  addMessage.send({
    role: "user",
    content: contentParts,
  });
});

const clearChat = handler(
  (
    _: never,
    { messages, pending }: {
      messages: Cell<Array<BuiltInLLMMessage>>;
      pending: Cell<boolean | undefined>;
    },
  ) => {
    messages.set([]);
    pending.set(false);
  },
);

type ChatInput = {
  messages: Default<Array<BuiltInLLMMessage>, []>;
  tools: any;
  theme?: any;
};

type PromptAttachment = {
  id: string;
  name: string;
  type: "file" | "clipboard" | "mention";
  data?: any; // File | Blob | string
  charm?: any;
};

type ChatOutput = {
  messages: Array<BuiltInLLMMessage>;
  pending: boolean | undefined;
  addMessage: Stream<BuiltInLLMMessage>;
  clearChat: Stream<void>;
  cancelGeneration: Stream<void>;
  title?: string;
  attachments: Array<PromptAttachment>;
  tools: any;
  ui: {
    chatLog: VNode;
    promptInput: VNode;
    attachmentsAndTools: VNode;
  };
};

export const TitleGenerator = recipe<
  { model?: string; messages: Array<BuiltInLLMMessage> }
>("Title Generator", ({ model, messages }) => {
  const titleMessages = derive(messages, (m) => {
    if (!m || m.length === 0) return "";

    const messageCount = 2;
    const selectedMessages = m.slice(0, messageCount).filter(Boolean);

    if (selectedMessages.length === 0) return "";

    return selectedMessages.map((msg) => JSON.stringify(msg)).join("\n");
  });

  const { result } = generateObject({
    system:
      "Generate at most a 3-word title based on the following content, respond with NOTHING but the literal title text.",
    prompt: titleMessages,
    model,
    schema: {
      type: "object",
      properties: {
        title: {
          type: "string",
          description: "The title of the chat",
        },
      },
      required: ["title"],
    },
  });

  const title = derive(result, (t) => {
    return t?.title || "Untitled Chat";
  });

  return title;
});

const navigateToAttachment = handler<
  { id: string },
  { allAttachments: Array<PromptAttachment> }
>(({ id }, { allAttachments }) => {
  const attachment = allAttachments.find((a) => a.id === id);

  return navigateTo(attachment?.charm);
});

const listAttachments = handler<
  { result: Cell<string> },
  { allAttachments: Array<PromptAttachment> }
>(({ result }, { allAttachments }) => {
  result.set(JSON.stringify(allAttachments.map((attachment) => ({
    id: attachment.id,
    name: attachment.name,
    type: attachment.type,
  }))));
});

const addAttachmentTool = handler<
  {
    mentionableName: string;
  },
  {
    mentionable: Array<MentionableCharm>;
    allAttachments: Cell<Array<PromptAttachment>>;
  }
>(({ mentionableName }, { mentionable, allAttachments }) => {
  const charm = mentionable.find((c) => c[NAME] === mentionableName);

  // borrowed from `ct-prompt-input` to match
  const id = `attachment-${Date.now()}-${
    Math.random().toString(36).substring(2, 9)
  }`;

  if (!charm) {
    throw new Error(
      `Unknown mentionable "${mentionableName}", cannot add attachment.`,
    );
  }

  allAttachments.push({
    id,
    name: mentionableName,
    type: "mention",
    charm,
  });
});

const removeAttachmentTool = handler<
  {
    mentionableName: string;
  },
  {
    allAttachments: Cell<Array<PromptAttachment>>;
  }
>(({ mentionableName }, { allAttachments }) => {
  allAttachments.set(
    allAttachments.get().filter((attachment) =>
      attachment.name !== mentionableName
    ),
  );
});

const listMentionable = handler<
  {
    /** A cell to store the result text */
    result: Cell<string>;
  },
  { mentionable: MentionableCharm[] }
>(
  (args, state) => {
    try {
      const namesList = state.mentionable.map((charm) => charm[NAME]);
      args.result.set(JSON.stringify(namesList));
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

export default recipe<ChatInput, ChatOutput>(
  "Chat",
  ({ messages, tools, theme }) => {
    const model = cell<string>("anthropic:claude-sonnet-4-5");
    const allAttachments = cell<Array<PromptAttachment>>([]);
    const mentionable = schemaifyWish<MentionableCharm[]>("#mentionable");

    // Derive tools from attachments
    const dynamicTools = derive(allAttachments, (attachments) => {
      const tools: Record<string, any> = {};

      for (const attachment of attachments || []) {
        if (attachment.type === "mention" && attachment.charm) {
          const charmName = attachment.charm[NAME] || "Charm";
          tools[charmName] = {
            charm: attachment.charm,
            description: `Handlers from ${charmName}`,
          };
        }
      }

      return tools;
    });

    const attachmentTools = {
      navigateToAttachment: {
        description:
          "Navigate to a mentionable by its ID in the attachments array.",
        handler: navigateToAttachment({ allAttachments }),
      },
      listAttachments: {
        description: "List all attachments in the attachments array.",
        handler: listAttachments({ allAttachments }),
      },
      listMentionable: {
        description: "List all mentionable NAMEs in the space.",
        handler: listMentionable({ mentionable }),
      },
      addAttachment: {
        description:
          "Add a new attachment to the attachments array by its mentionable NAME.",
        handler: addAttachmentTool({ mentionable, allAttachments }),
      },
      removeAttachment: {
        description:
          "Remove an attachment from the attachments array by its mentionable NAME.",
        handler: removeAttachmentTool({ allAttachments }),
      },
    };

    // Merge static and dynamic tools
    const mergedTools = derive(
      [tools, dynamicTools, attachmentTools],
      ([staticTools, dynamic, attachments]: [any, any, any]) => ({
        ...staticTools,
        ...dynamic,
        ...attachments,
      }),
    );

    const { addMessage, cancelGeneration, pending, flattenedTools } = llmDialog(
      {
        system: "You are a helpful assistant with some tools.",
        messages,
        tools: mergedTools,
        model,
      },
    );

    const { result } = fetchData({
      url: "/api/ai/llm/models",
      mode: "json",
    });

    const items = derive(result, (models) => {
      if (!models) return [];
      const items = Object.keys(models as any).map((key) => ({
        label: key,
        value: key,
      }));
      return items;
    });

    const title = TitleGenerator({ model, messages });

    const promptInput = (
      <ct-prompt-input
        slot="footer"
        placeholder="Ask the LLM a question..."
        pending={pending}
        $mentionable={mentionable}
        modelItems={items}
        $model={model}
        onct-send={sendMessage({ addMessage, allAttachments })}
        onct-stop={cancelGeneration}
        onct-attachment-add={addAttachment({ allAttachments })}
        onct-attachment-remove={removeAttachment({ allAttachments })}
      />
    );

    const chatLog = (
      <ct-vscroll
        style="padding: 1rem;"
        flex
        showScrollbar
        fadeEdges
        snapToBottom
      >
        <ct-chat
          theme={theme}
          $messages={messages}
          pending={pending}
          tools={flattenedTools}
        />
      </ct-vscroll>
    );

    const attachmentsAndTools = (
      <ct-hstack align="center" gap="1">
        <ct-attachments-bar
          attachments={allAttachments}
          removable
          onct-remove={removeAttachment({ allAttachments })}
        />
        <ct-tools-chip tools={flattenedTools} />
        <ct-button
          variant="pill"
          type="button"
          title="Clear chat"
          onClick={clearChat({
            messages,
            pending,
          })}
        >
          Clear
        </ct-button>
      </ct-hstack>
    );

    return {
      [NAME]: title,
      [UI]: (
        <ct-screen>
          <ct-vstack slot="header">
            <ct-heading level={4}>{title}</ct-heading>
            {attachmentsAndTools}
          </ct-vstack>

          {chatLog}

          {promptInput}
        </ct-screen>
      ),
      messages,
      pending,
      addMessage,
      clearChat: clearChat({
        messages,
        pending,
      }),
      cancelGeneration,
      title,
      attachments: allAttachments,
      tools: flattenedTools,
      ui: {
        chatLog,
        promptInput,
        attachmentsAndTools,
      },
    };
  },
);
"},{"name":"/chatbot-outliner.tsx","contents":"/// <cts-enable />
import {
  BuiltInLLMMessage,
  Cell,
  Default,
  derive,
  handler,
  ifElse,
  JSONSchema,
  NAME,
  navigateTo,
  OpaqueRef,
  recipe,
  UI,
  wish,
} from "commontools";

import Chat from "./chatbot.tsx";
import { MentionableCharm } from "./backlinks-index.tsx";

type Charm = any;

type OutlinerNode = {
  body: Default<string, "">;
  children: Default<OutlinerNode[], []>;
  attachments: Default<OpaqueRef<any>[], []>;
};

type Outliner = {
  root: OutlinerNode;
};

type PageResult = {
  outline: Default<
    Outliner,
    { root: { body: ""; children: []; attachments: [] } }
  >;
};

export type PageInput = {
  outline: Outliner;
};

const handleCharmLinkClick = handler<
  {
    detail: {
      charm: Cell<Charm>;
    };
  },
  Record<string, never>
>(({ detail }, _) => {
  return navigateTo(detail.charm);
});

function getMentionable() {
  return derive<MentionableCharm[], MentionableCharm[]>(
    wish<MentionableCharm[]>("#mentionable"),
    (i) => i,
  );
}

export const Page = recipe<PageInput>(
  "Page",
  ({ outline }) => {
    const mentionable = getMentionable();

    return {
      [NAME]: "Page",
      [UI]: (
        <ct-outliner
          $value={outline as any}
          $mentionable={mentionable}
          oncharm-link-click={handleCharmLinkClick({})}
        />
      ),
      outline,
    };
  },
);

type LLMTestInput = {
  title: Default<string, "LLM Test">;
  messages: Default<Array<BuiltInLLMMessage>, []>;
  expandChat: Default<boolean, false>;
  outline: Default<
    Outliner,
    { root: { body: "Untitled Page"; children: []; attachments: [] } }
  >;
};

type LLMTestResult = {
  messages: Default<Array<BuiltInLLMMessage>, []>;
};

// put a node at the end of the outline (by appending to root.children)
const appendOutlinerNode = handler<
  {
    /** The text content/title of the outliner node to be appended */
    body: string;
    /** A cell to store the result message indicating success or error */
    result: Cell<string>;
  },
  { outline: Cell<Outliner> }
>(
  (args, state) => {
    try {
      (state.outline.key("root").key("children")).push({
        body: args.body,
        children: [],
        attachments: [],
      });

      args.result.set(
        `${state.outline.key("root").key("children").get().length} nodes`,
      );
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

export default recipe<LLMTestInput, LLMTestResult>(
  "Outliner",
  ({ title, expandChat, messages, outline }) => {
    const tools = {
      appendOutlinerNode: {
        description: "Add a new outliner node.",
        inputSchema: {
          type: "object",
          properties: {
            body: {
              type: "string",
              description: "The title of the new node.",
            },
          },
          required: ["body"],
        } as JSONSchema,
        handler: appendOutlinerNode({ outline }),
      },
    };

    const chat = Chat({ messages, tools });

    return {
      [NAME]: title,
      [UI]: (
        <ct-screen>
          <ct-hstack justify="between" slot="header">
            <div></div>
            <div>
              <ct-checkbox $checked={expandChat}>Show Chat</ct-checkbox>
            </div>
          </ct-hstack>

          <ct-autolayout tabNames={["Chat", "Tools"]}>
            <ct-screen>
              <div slot="header">
                <ct-input
                  $value={title}
                  placeholder="Enter title..."
                />
              </div>

              <ct-vscroll flex showScrollbar fadeEdges snapToBottom>
                <ct-vstack data-label="Tools">
                  <Page outline={outline} />
                </ct-vstack>
              </ct-vscroll>
            </ct-screen>

            {ifElse(
              expandChat,
              chat,
              null,
            )}
          </ct-autolayout>
        </ct-screen>
      ),
      messages,
    };
  },
);
"},{"name":"/note.tsx","contents":"/// <cts-enable />
import {
  Cell,
  cell,
  Default,
  derive,
  handler,
  NAME,
  navigateTo,
  Opaque,
  OpaqueRef,
  patternTool,
  recipe,
  UI,
  wish,
} from "commontools";
import { type MentionableCharm } from "./backlinks-index.tsx";
type Input = {
  title: Default<string, "Untitled Note">;
  content: Default<string, "">;
};

type Output = {
  mentioned: Default<Array<MentionableCharm>, []>;
  backlinks: MentionableCharm[];

  /** The content of the note */
  content: Default<string, "">;
  grep: OpaqueRef<{ query: string }>;
  editContent: OpaqueRef<{ detail: { value: string } }>;
};

const _updateTitle = handler<
  { detail: { value: string } },
  { title: Cell<string> }
>(
  (event, state) => {
    state.title.set(event.detail?.value ?? "");
  },
);

const _updateContent = handler<
  { detail: { value: string } },
  { content: Cell<string> }
>(
  (event, state) => {
    state.content.set(event.detail?.value ?? "");
  },
);

const handleCharmLinkClick = handler<
  {
    detail: {
      charm: Cell<MentionableCharm>;
    };
  },
  Record<string, never>
>(({ detail }, _) => {
  return navigateTo(detail.charm);
});

const handleNewBacklink = handler<
  {
    detail: {
      text: string;
      charmId: any;
      charm: Cell<MentionableCharm>;
      navigate: boolean;
    };
  },
  {
    mentionable: Cell<MentionableCharm[]>;
  }
>(({ detail }, { mentionable }) => {
  console.log("new charm", detail.text, detail.charmId);

  if (detail.navigate) {
    return navigateTo(detail.charm);
  } else {
    mentionable.push(detail.charm as unknown as MentionableCharm);
  }
});

/** This edits the content */
const handleEditContent = handler<
  { detail: { value: string } },
  { content: Cell<string> }
>(
  ({ detail }, { content }) => {
    content.set(detail.value);
  },
);

const handleCharmLinkClicked = handler<void, { charm: Cell<MentionableCharm> }>(
  (_, { charm }) => {
    return navigateTo(charm);
  },
);

function schemaifyWish<T>(path: string, def: T | Opaque<T>) {
  return derive<T, T>(wish<T>(path, def as Opaque<T>), (i) => i);
}

const Note = recipe<Input, Output>(
  "Note",
  ({ title, content }) => {
    const mentionable = schemaifyWish<MentionableCharm[]>(
      "#mentionable",
      [],
    );
    const mentioned = cell<MentionableCharm[]>([]);

    // populated in backlinks-index.tsx
    const backlinks = cell<MentionableCharm[]>([]);

    // The only way to serialize a pattern, apparently?
    const pattern = derive(undefined, () => JSON.stringify(Note));

    return {
      [NAME]: title,
      [UI]: (
        <ct-screen>
          <div slot="header">
            <ct-input
              $value={title}
              placeholder="Enter title..."
            />
          </div>

          <ct-code-editor
            $value={content}
            $mentionable={mentionable}
            $mentioned={mentioned}
            $pattern={pattern}
            onbacklink-click={handleCharmLinkClick({})}
            onbacklink-create={handleNewBacklink({ mentionable })}
            language="text/markdown"
            theme="light"
            wordWrap
            tabIndent
            lineNumbers
          />

          <ct-hstack slot="footer">
            {backlinks?.map((charm) => (
              <ct-button
                onClick={handleCharmLinkClicked({ charm })}
              >
                {charm?.[NAME]}
              </ct-button>
            ))}
          </ct-hstack>
        </ct-screen>
      ),
      title,
      content,
      mentioned,
      backlinks,
      grep: patternTool(
        ({ query, content }: { query: string; content: string }) => {
          return derive({ query, content }, ({ query, content }) => {
            return content.split("\n").filter((c) => c.includes(query));
          });
        },
        { content },
      ),
      editContent: handleEditContent({ content }),
    };
  },
);

export default Note;
"},{"name":"/backlinks-index.tsx","contents":"/// <cts-enable />
import { Cell, lift, NAME, OpaqueRef, recipe, UI } from "commontools";

export type MentionableCharm = {
  [NAME]?: string;
  mentioned: MentionableCharm[];
  backlinks: MentionableCharm[];
};

export type WriteableBacklinks = {
  mentioned: WriteableBacklinks[];
  backlinks: Cell<WriteableBacklinks[]>;
};

type Input = {
  allCharms: MentionableCharm[];
};

type Output = {
  mentionable: MentionableCharm[];
};

const computeIndex = lift<
  { allCharms: WriteableBacklinks[] },
  void
>(
  ({ allCharms }) => {
    const cs = allCharms ?? [];

    for (const c of cs) {
      c.backlinks?.set([]);
    }

    for (const c of cs) {
      const mentions = c.mentioned ?? [];
      for (const m of mentions) {
        m?.backlinks?.push(c);
      }
    }
  },
);

/**
 * BacklinksIndex builds a map of backlinks across all charms and exposes a
 * unified mentionable list for consumers like editors.
 *
 * Behavior:
 * - Backlinks are computed by scanning each charm's `mentioned` list and
 *   mapping mention target -> list of source charms.
 * - Mentionable list is a union of:
 *   - every charm in `allCharms`
 *   - any items a charm exports via a `mentionable` property
 *     (either an array of charms or a Cell of such an array)
 *
 * The backlinks map is keyed by a charm's `content` value (falling back to
 * its `[NAME]`). This mirrors how existing note patterns identify notes when
 * computing backlinks locally.
 */
const BacklinksIndex = recipe<Input, Output>(
  "BacklinksIndex",
  ({ allCharms }) => {
    computeIndex({
      allCharms: allCharms as unknown as OpaqueRef<WriteableBacklinks[]>,
    });

    // Compute mentionable list from allCharms via lift, then mirror that into
    // a real Cell for downstream consumers that expect a Cell.
    const computeMentionable = lift<
      { allCharms: any[] },
      MentionableCharm[]
    >(({ allCharms }) => {
      const cs = allCharms ?? [];
      const out: MentionableCharm[] = [];
      for (const c of cs) {
        out.push(c);
        const exported = (c as unknown as {
          mentionable?: MentionableCharm[] | { get?: () => MentionableCharm[] };
        }).mentionable;
        if (Array.isArray(exported)) {
          for (const m of exported) if (m) out.push(m);
        } else if (exported && typeof (exported as any).get === "function") {
          const arr = (exported as { get: () => MentionableCharm[] }).get() ??
            [];
          for (const m of arr) if (m) out.push(m);
        }
      }
      return out;
    });

    return {
      [NAME]: "BacklinksIndex",
      [UI]: undefined,
      mentionable: computeMentionable({ allCharms }),
    };
  },
);

export default BacklinksIndex;
"},{"name":"/chatbot-list-view.tsx","contents":"/// <cts-enable />
import {
  Cell,
  cell,
  Default,
  derive,
  handler,
  ID,
  ifElse,
  lift,
  NAME,
  navigateTo,
  OpaqueRef,
  recipe,
  toSchema,
  UI,
  wish,
} from "commontools";

import Chat from "./chatbot-note-composed.tsx";
import { ListItem } from "./common-tools.tsx";

import { type MentionableCharm } from "./backlinks-index.tsx";
type CharmEntry = {
  [ID]: string; // randomId is a string
  local_id: string; // same as ID but easier to access
  charm: any;
};

type Input = {
  selectedCharm: Default<{ charm: any }, { charm: undefined }>;
  charmsList: Default<CharmEntry[], []>;
  theme?: {
    accentColor: Default<string, "#3b82f6">;
    fontFace: Default<string, "system-ui, -apple-system, sans-serif">;
    borderRadius: Default<string, "0.5rem">;
  };
};

type Output = {
  selectedCharm: Default<{ charm: any }, { charm: undefined }>;
  // Expose a mentionable list aggregated from local chat entries
  // Returned as an opaque ref to an array (not a Cell), suitable for
  // upstream aggregators that read exported mentionables.
  mentionable?: MentionableCharm[];
};

const removeChat = handler<
  unknown,
  {
    charmsList: Cell<CharmEntry[]>;
    id: string;
    selectedCharm: Cell<Default<{ charm: any }, { charm: undefined }>>;
  }
>(
  (
    _,
    { charmsList, id, selectedCharm },
  ) => {
    const list = charmsList.get();
    const index = list.findIndex((entry) => entry.local_id === id);
    if (index === -1) return;

    const removed = list[index];
    const next = [...list];
    next.splice(index, 1);
    charmsList.set(next);

    // If we removed the currently selected charm, choose a new selection.
    const current = selectedCharm.get();
    if (current?.charm === removed.charm) {
      const replacement = next[index] ?? next[index - 1];
      if (replacement) {
        selectedCharm.set({ charm: replacement.charm });
      } else {
        selectedCharm.set({ charm: undefined as unknown as any });
      }
    }
  },
);

// this will be called whenever charm or selectedCharm changes
// pass isInitialized to make sure we dont call this each time
// we change selectedCharm, otherwise creates a loop
const storeCharm = lift(
  toSchema<{
    charm: any;
    selectedCharm: Cell<Default<{ charm: any }, { charm: undefined }>>;
    charmsList: Cell<CharmEntry[]>;
    allCharms: Cell<MentionableCharm[]>;
    theme?: {
      accentColor: Default<string, "#3b82f6">;
      fontFace: Default<string, "system-ui, -apple-system, sans-serif">;
      borderRadius: Default<string, "0.5rem">;
    };
    isInitialized: Cell<boolean>;
  }>(),
  undefined,
  ({ charm, selectedCharm, charmsList, isInitialized, allCharms: _ }) => { // Not including `allCharms` is a compile error...
    if (!isInitialized.get()) {
      console.log(
        "storeCharm storing charm:",
        charm,
      );
      selectedCharm.set({ charm });

      // create the chat charm with a custom name including a random suffix
      const randomId = Math.random().toString(36).substring(2, 10); // Random 8-char string
      charmsList.push({ [ID]: randomId, local_id: randomId, charm });

      isInitialized.set(true);
      return charm;
    } else {
      console.log("storeCharm: already initialized");
    }
    return undefined;
  },
);

const populateChatList = lift(
  toSchema<{
    charmsList: CharmEntry[];
    allCharms: Cell<any[]>;
    selectedCharm: Cell<{ charm: any }>;
  }>(),
  undefined,
  (
    { charmsList, allCharms, selectedCharm },
  ) => {
    if (charmsList.length === 0) {
      const isInitialized = cell(false);
      return storeCharm({
        charm: Chat({
          title: "New Chat",
          messages: [],
        }),
        selectedCharm,
        charmsList,
        allCharms,
        isInitialized: isInitialized as unknown as Cell<boolean>,
      });
    }

    return charmsList;
  },
);

const createChatRecipe = handler<
  unknown,
  {
    selectedCharm: Cell<{ charm: any }>;
    charmsList: Cell<CharmEntry[]>;
    allCharms: Cell<MentionableCharm[]>;
  }
>(
  (_, { selectedCharm, charmsList, allCharms }) => {
    const isInitialized = cell(false);

    const charm = Chat({
      title: "New Chat",
      messages: [],
    });
    // store the charm ref in a cell (pass isInitialized to prevent recursive calls)
    return storeCharm({
      charm,
      selectedCharm,
      charmsList: charmsList as unknown as OpaqueRef<CharmEntry[]>,
      allCharms,
      isInitialized: isInitialized as unknown as Cell<boolean>,
    });
  },
);

const selectCharm = handler<
  unknown,
  { selectedCharm: Cell<{ charm: any }>; charm: any }
>(
  (_, { selectedCharm, charm }) => {
    console.log("selectCharm: updating selectedCharm to ", charm);
    selectedCharm.set({ charm });
    return selectedCharm;
  },
);

const logCharmsList = lift<
  { charmsList: Cell<CharmEntry[]> },
  Cell<CharmEntry[]>
>(
  ({ charmsList }) => {
    console.log("logCharmsList: ", charmsList.get());
    return charmsList;
  },
);

const _handleCharmLinkClicked = handler(
  (_: any, { charm }: { charm: Cell<MentionableCharm> }) => {
    return navigateTo(charm);
  },
);

const _merge = lift(
  (
    { allCharms, charmsList }: { allCharms: any[]; charmsList: CharmEntry[] },
  ) => {
    return [...charmsList.map((c) => c.charm), ...allCharms];
  },
);

const getSelectedCharm = lift<
  { entry: { charm: any | undefined } },
  {
    chat: unknown;
    list: ListItem[];
  } | undefined
>(
  ({ entry }) => {
    return entry?.charm;
  },
);

const getCharmName = lift(({ charm }: { charm: any }) => {
  return charm?.[NAME] || "Unknown";
});

const extractLocalMentionable = lift<
  { list: CharmEntry[] },
  MentionableCharm[]
>(({ list }) => {
  const out: MentionableCharm[] = [];
  for (const entry of list) {
    const c = entry.charm;
    out.push(c.chat);
  }
  return out;
});

// create the named cell inside the recipe body, so we do it just once
export default recipe<Input, Output>(
  "Launcher",
  ({ selectedCharm, charmsList, theme }) => {
    const allCharms = derive<MentionableCharm[], MentionableCharm[]>(
      wish<MentionableCharm[]>("#allCharms", []),
      (c) => c,
    );
    logCharmsList({ charmsList: charmsList as unknown as Cell<CharmEntry[]> });

    populateChatList({
      selectedCharm: selectedCharm as unknown as Cell<
        Pick<CharmEntry, "charm">
      >,
      charmsList,
      allCharms,
    });

    const selected = getSelectedCharm({ entry: selectedCharm });

    // Aggregate mentionables from the local charms list so that this
    // container exposes its child chat charms as mention targets.
    const localMentionable = extractLocalMentionable({ list: charmsList });

    const localTheme = theme ?? {
      accentColor: cell("#3b82f6"),
      fontFace: cell("system-ui, -apple-system, sans-serif"),
      borderRadius: cell("0.5rem"),
    };

    return {
      [NAME]: "Launcher",
      [UI]: (
        <ct-theme theme={localTheme as any}>
          <ct-screen>
            <div slot="header">
              <ct-toolbar dense sticky>
                <div slot="start">
                  <ct-button
                    id="new-chat-btn"
                    onClick={createChatRecipe({
                      selectedCharm,
                      charmsList,
                      allCharms,
                    })}
                  >
                    Create New Chat
                    <ct-kbd>alt+N</ct-kbd>
                  </ct-button>
                </div>
              </ct-toolbar>

              {/* Keyboard shortcuts */}
              <ct-keybind
                code="KeyN"
                alt
                preventDefault
                onct-keybind={createChatRecipe({
                  selectedCharm,
                  charmsList,
                  allCharms,
                })}
              />
            </div>
            <ct-autolayout
              leftOpen
              rightOpen={false}
            >
              {/* workaround: this seems to correctly start the sub-recipes on a refresh while directly rendering does not */}
              {/* this should be fixed after the builder-refactor (DX1) */}
              <ct-screen>
                <ct-render $cell={selected.chat} />
              </ct-screen>

              <aside slot="left">
                <div>
                  <ct-heading level={3}>Chat List</ct-heading>
                </div>
                <div role="list">
                  {charmsList.map((charmEntry) => (
                    <ct-list-item
                      onct-activate={selectCharm({
                        selectedCharm,
                        charm: charmEntry.charm,
                      })}
                    >
                      <span>{getCharmName({ charm: charmEntry.charm })}</span>
                      <span slot="meta">{charmEntry.local_id}</span>
                      <ct-button
                        slot="actions"
                        size="sm"
                        title="Delete Chat"
                        variant="destructive"
                        onClick={removeChat({
                          charmsList: charmsList as unknown as OpaqueRef<
                            CharmEntry[]
                          >,
                          id: charmEntry.local_id,
                          selectedCharm: selectedCharm as unknown as OpaqueRef<
                            Default<{ charm: any }, { charm: undefined }>
                          >,
                        })}
                      >
                        üóëÔ∏è
                      </ct-button>
                    </ct-list-item>
                  ))}
                </div>
              </aside>

              <aside slot="right">
                {ifElse(
                  selected,
                  <>
                    <ct-ct-collapsible>
                      <ct-heading slot="trigger" level={5} no-margin>
                        List
                      </ct-heading>
                      <ct-list $value={selected.list} />
                    </ct-ct-collapsible>
                  </>,
                  null,
                )}
                <ct-collapsible>
                  <ct-heading slot="trigger" level={5} no-margin>
                    Theme
                  </ct-heading>
                  <ct-vstack style="padding: 0.5rem 0; gap: 0.5rem;">
                    <ct-vstack>
                      <ct-text>Font Family</ct-text>
                      <ct-select
                        items={[
                          {
                            label: "System",
                            value: "system-ui, -apple-system, sans-serif",
                          },
                          {
                            label: "Monospace",
                            value: "ui-monospace, Consolas, monospace",
                          },
                          {
                            label: "Serif",
                            value: "Georgia, Times, serif",
                          },
                          {
                            label: "Sans Serif",
                            value: "Arial, Helvetica, sans-serif",
                          },
                        ]}
                        $value={localTheme.fontFace}
                      />
                    </ct-vstack>

                    <ct-vstack>
                      <ct-text>Accent Color</ct-text>
                      <ct-select
                        items={[
                          { label: "Blue", value: "#3b82f6" },
                          { label: "Purple", value: "#8b5cf6" },
                          { label: "Green", value: "#10b981" },
                          { label: "Red", value: "#ef4444" },
                          { label: "Orange", value: "#f97316" },
                          { label: "Pink", value: "#ec4899" },
                          { label: "Indigo", value: "#6366f1" },
                          { label: "Teal", value: "#14b8a6" },
                        ]}
                        $value={localTheme.accentColor}
                      />
                    </ct-vstack>

                    <ct-vstack>
                      <ct-text>Border Radius</ct-text>
                      <ct-select
                        items={[
                          { label: "None", value: "0px" },
                          { label: "Small", value: "0.25rem" },
                          { label: "Medium", value: "0.5rem" },
                          { label: "Large", value: "0.75rem" },
                          { label: "Extra Large", value: "1rem" },
                          { label: "Rounded", value: "1.5rem" },
                        ]}
                        $value={localTheme.borderRadius}
                      />
                    </ct-vstack>
                  </ct-vstack>
                </ct-collapsible>
              </aside>
            </ct-autolayout>
          </ct-screen>
        </ct-theme>
      ),
      selectedCharm,
      charmsList,
      // Expose the aggregated mentionables for parent-level indexing.
      mentionable: localMentionable,
    };
  },
);
"},{"name":"/omnibox-fab.tsx","contents":"/// <cts-enable />
import {
  Cell,
  cell,
  derive,
  handler,
  ifElse,
  NAME,
  recipe,
  UI,
} from "commontools";
import Chatbot from "./chatbot.tsx";
import { calculator, readWebpage, searchWeb } from "./common-tools.tsx";
import { MentionableCharm } from "./backlinks-index.tsx";

interface OmniboxFABInput {
  mentionable: Cell<MentionableCharm[]>;
}

const toggle = handler<any, { value: Cell<boolean> }>((_, { value }) => {
  value.set(!value.get());
});

const closeFab = handler<any, { fabExpanded: Cell<boolean> }>(
  (_, { fabExpanded }) => {
    fabExpanded.set(false);
  },
);

const dismissPeek = handler<
  any,
  { peekDismissedIndex: Cell<number>; assistantMessageCount: number }
>((_, { peekDismissedIndex, assistantMessageCount }) => {
  // Store the current assistant message count so we know which message was dismissed
  peekDismissedIndex.set(assistantMessageCount);
});

export default recipe<OmniboxFABInput>(
  "OmniboxFAB",
  ({ mentionable: _mentionable }) => {
    const omnibot = Chatbot({
      messages: [],
      tools: {
        searchWeb: {
          pattern: searchWeb,
        },
        readWebpage: {
          pattern: readWebpage,
        },
        calculator: {
          pattern: calculator,
        },
      },
    });

    const fabExpanded = cell(false);
    const showHistory = cell(false);
    const peekDismissedIndex = cell(-1); // Track which message index was dismissed

    // Derive assistant message count for dismiss tracking
    const assistantMessageCount = derive(
      omnibot.messages,
      (messages) => messages.filter((m) => m.role === "assistant").length,
    );

    // Derive latest assistant message for peek
    const latestAssistantMessage = derive(omnibot.messages, (messages) => {
      if (!messages || messages.length === 0) return null;

      for (let i = messages.length - 1; i >= 0; i--) {
        const msg = messages[i];
        if (msg.role === "assistant") {
          const content = typeof msg.content === "string"
            ? msg.content
            : msg.content.map((part: any) => {
              if (part.type === "text") return part.text;
              return "";
            }).join("");

          return content;
        }
      }
      return null;
    });

    return {
      [NAME]: "OmniboxFAB",
      messages: omnibot.messages,
      [UI]: (
        <ct-fab
          expanded={fabExpanded}
          variant="primary"
          position="bottom-right"
          pending={omnibot.pending}
          $previewMessage={latestAssistantMessage}
          onct-fab-backdrop-click={closeFab({ fabExpanded })}
          onct-fab-escape={closeFab({ fabExpanded })}
          onClick={toggle({ value: fabExpanded })}
        >
          <div style="width: 100%; display: flex; flex-direction: column; max-height: 580px;">
            {/* Chevron at top - the "handle" for the drawer */}
            <div style="border-bottom: 1px solid #e5e5e5; flex-shrink: 0;">
              <ct-chevron-button
                expanded={showHistory}
                loading={omnibot.pending}
                onct-toggle={toggle({ value: showHistory })}
              />
            </div>

            <div
              style={derive(
                showHistory,
                (show) =>
                  `flex: ${
                    show ? "1" : "0"
                  }; min-height: 0; display: flex; flex-direction: column; opacity: ${
                    show ? "1" : "0"
                  }; max-height: ${
                    show ? "480px" : "0"
                  }; overflow: hidden; transition: opacity 300ms ease, max-height 400ms cubic-bezier(0.34, 1.56, 0.64, 1), flex 400ms cubic-bezier(0.34, 1.56, 0.64, 1); pointer-events: ${
                    show ? "auto" : "none"
                  };`,
              )}
            >
              <div style="padding: .25rem; flex-shrink: 0;">
                {omnibot.ui.attachmentsAndTools}
              </div>
              <div style="flex: 1; overflow-y: auto; min-height: 0;">
                {omnibot.ui.chatLog}
              </div>
            </div>

            {ifElse(
              derive(
                [
                  showHistory,
                  latestAssistantMessage,
                  peekDismissedIndex,
                  assistantMessageCount,
                ],
                ([show, msg, dismissedIdx, count]) =>
                  !show && msg && count !== dismissedIdx,
              ),
              <div style="margin: .5rem; margin-bottom: 0; padding: 0; flex-shrink: 0; position: relative;">
                <ct-button
                  variant="ghost"
                  size="icon"
                  onClick={dismissPeek({
                    peekDismissedIndex,
                    assistantMessageCount,
                  })}
                  style="position: absolute; top: 0px; right: 0px; z-index: 1; font-size: 16px;"
                  title="Dismiss"
                >
                  √ó
                </ct-button>
                <div
                  onClick={toggle({ value: showHistory })}
                  style="cursor: pointer;"
                >
                  <ct-chat-message
                    role="assistant"
                    compact
                    content={latestAssistantMessage}
                    pending={omnibot.pending}
                  />
                </div>
              </div>,
              null,
            )}

            {/* Prompt input - always at bottom */}
            <div style="padding: 0.5rem; flex-shrink: 0;">
              {omnibot.ui.promptInput}
            </div>
          </div>
        </ct-fab>
      ),
      fabExpanded,
    };
  },
);
"},{"name":"/chatbot-note-composed.tsx","contents":"/// <cts-enable />
import {
  BuiltInLLMMessage,
  Cell,
  cell,
  Default,
  derive,
  handler,
  NAME,
  navigateTo,
  Opaque,
  OpaqueRef,
  recipe,
  wish,
} from "commontools";

import Chat from "./chatbot.tsx";
import Note from "./note.tsx";
import {
  addListItem,
  calculator,
  ListItem,
  readListItems,
  readWebpage,
  searchWeb,
} from "./common-tools.tsx";

import { type MentionableCharm } from "./backlinks-index.tsx";

function schemaifyWish<T>(path: string, def: Opaque<T>) {
  return derive<T, T>(wish<T>(path, def), (i) => i);
}

type ChatbotNoteInput = {
  title: Default<string, "LLM Test">;
  messages: Default<Array<BuiltInLLMMessage>, []>;
};

type ChatbotNoteResult = {
  messages: Default<Array<BuiltInLLMMessage>, []>;
  chat: any;
  list: Default<ListItem[], []>;
  // Optional: expose sub-charms as mentionable targets
  mentionable?: MentionableCharm[];
};

const newNote = handler<
  {
    /** The text content of the note */
    title: string;
    content?: string;
    /** A cell to store the result message indicating success or error */
    result: Cell<string>;
  },
  { allCharms: Cell<MentionableCharm[]>; index: any }
>(
  (args, _) => {
    try {
      const n = Note({
        title: args.title,
        content: args.content || "",
      });

      args.result.set(
        `Created note ${args.title}!`,
      );

      // TODO(bf): we have to navigate here until DX1 lands
      // then we go back to pushing to allCharms
      return navigateTo(n);
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

const listMentionable = handler<
  {
    /** A cell to store the result text */
    result: Cell<string>;
  },
  { mentionable: MentionableCharm[] }
>(
  (args, state) => {
    try {
      const namesList = state.mentionable.map((charm) => charm[NAME]);
      args.result.set(JSON.stringify(namesList));
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

const readContentByIndex = handler<
  {
    /** A cell to store the result text */
    index: number;
    result: Cell<string>;
  },
  { allNotes: Note[] }
>(
  (args, state) => {
    try {
      args.result.set(
        state.allNotes[args.index]?.content || "No content found",
      );
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

type Note = MentionableCharm & { content: string };
const editContentByIndex = handler<
  {
    /** The index of the note to edit */
    index: number;
    /** The new text content of the note */
    body: string;
    /** A cell to store the result message indicating success or error */
    result: Cell<string>;
  },
  { allNotes: Cell<Note[]> }
>(
  (args, state) => {
    try {
      const charms = state.allNotes.get();
      if (args.index < 0 || args.index >= charms.length) {
        args.result.set(`Error: Invalid index ${args.index}`);
        return;
      }

      state.allNotes.key(args.index).key("content").set(args.body);
      args.result.set(`Updated note at index ${args.index}!`);
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

const navigateToNote = handler<
  {
    /** The index of the note to navigate to */
    index: number;
    /** A cell to store the result message indicating success or error */
    result: Cell<string>;
  },
  { allCharms: Cell<MentionableCharm[]> }
>(
  (args, state) => {
    try {
      const charms = state.allCharms.get();
      if (args.index < 0 || args.index >= charms.length) {
        args.result.set(`Error: Invalid index ${args.index}`);
        return;
      }

      const targetCharm = charms[args.index];
      args.result.set(`Navigating to note: ${targetCharm[NAME]}`);

      return navigateTo(state.allCharms.key(args.index));
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

type BacklinksIndex = {
  mentionable: MentionableCharm[];
};

export default recipe<ChatbotNoteInput, ChatbotNoteResult>(
  "Chatbot + Note",
  ({ title, messages }) => {
    const allCharms = schemaifyWish<MentionableCharm[]>("#allCharms", []);
    const index = schemaifyWish<BacklinksIndex>("#default/backlinksIndex", {
      mentionable: [],
    });
    const mentionable = schemaifyWish<MentionableCharm[]>(
      "#mentionable",
      [],
    );

    const list = cell<ListItem[]>([]);

    const tools = {
      searchWeb: {
        pattern: searchWeb,
      },
      readWebpage: {
        pattern: readWebpage,
      },
      calculator: {
        pattern: calculator,
      },
      addListItem: {
        handler: addListItem({ list }),
      },
      readListItems: {
        handler: readListItems({ list }),
      },
      listMentionable: {
        description:
          "List all mentionable items titles (read the body with readNoteByIndex).",
        handler: listMentionable({ mentionable }),
      },
      readContentByIndex: {
        description:
          "Read the content of a mentionable by its index in the listMentionable() list (if possible)",
        handler: readContentByIndex({
          allNotes: mentionable as unknown as OpaqueRef<Note[]>,
        }),
      },
      editContentByIndex: {
        description:
          "Edit the content of a mentionable by its index in the listMentionable() list (if possible)",
        handler: editContentByIndex({
          allNotes: mentionable as unknown as OpaqueRef<Note[]>,
        }),
      },
      navigateToNote: {
        description:
          "Navigate to a mentionable by its index in the listMentionable() list.",
        handler: navigateToNote({
          allCharms: allCharms as unknown as OpaqueRef<MentionableCharm[]>,
        }),
      },
      newNote: {
        description: "Create a new note instance",
        handler: newNote({
          allCharms: allCharms as unknown as OpaqueRef<MentionableCharm[]>,
          index: index as unknown as OpaqueRef<any>,
        }),
      },
    };

    const chat = Chat({ messages, tools });

    return {
      [NAME]: title,
      chat,
      messages,
      list,
      // Expose sub-charms as mentionable targets
      mentionable: [chat as unknown as MentionableCharm],
    };
  },
);
"},{"name":"/common-tools.tsx","contents":"/// <cts-enable />
import {
  BuiltInLLMTool,
  Cell,
  derive,
  fetchData,
  handler,
  ifElse,
  recipe,
} from "commontools";

///// COMMON TOOLS (get it?) ////

/**
 * Calculate the result of a mathematical expression.
 * Supports +, -, *, /, and parentheses.
 */
type CalculatorRequest = {
  /** The mathematical expression to evaluate. */
  expression: string;
  /** The base to use for the calculation. */
  base?: number;
};

export const calculator = recipe<
  CalculatorRequest,
  string | { error: string }
>("Calculator", ({ expression, base }) => {
  return derive({ expression, base }, ({ expression, base }) => {
    const sanitized = expression.replace(/[^0-9+\-*/().\s]/g, "");
    let sanitizedBase = Number(base);
    if (
      Number.isNaN(sanitizedBase) || sanitizedBase < 2 || sanitizedBase > 36
    ) {
      sanitizedBase = 10;
    }
    let result;
    try {
      result = Function(
        `"use strict"; return Number(${sanitized}).toString(${sanitizedBase})`,
      )();
    } catch (error) {
      result = { error: (error as any)?.message || "<error>" };
    }
    return result;
  });
});

/** Add an item to the list. */
type AddListItemRequest = {
  /** The item to add to the list. */
  item: string;
  result: Cell<string>;
};

/** Read all items from the list. */
type ReadListItemsRequest = {
  result: Cell<string>;
};

export type ListItem = {
  title: string;
};

export const addListItem = handler<
  AddListItemRequest,
  { list: Cell<ListItem[]> }
>(
  (args, state) => {
    try {
      state.list.push({ title: args.item });
      args.result.set(`${state.list.get().length} items`);
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

export const readListItems = handler<
  ReadListItemsRequest,
  { list: ListItem[] }
>(
  (args, state) => {
    try {
      const items = state.list;
      if (items.length === 0) {
        args.result.set("The list is empty");
      } else {
        const itemList = items.map((item, index) =>
          `${index + 1}. ${item.title}`
        ).join("\n");
        args.result.set(`List items (${items.length} total):\n${itemList}`);
      }
    } catch (error) {
      args.result.set(`Error: ${(error as any)?.message || "<error>"}`);
    }
  },
);

/** Search the web for information. */
type SearchQuery = {
  /** The query to search the web for. */
  query: string;
};

type SearchWebResult = {
  results: {
    title: string;
    url: string;
    description: string;
  }[];
};

export const searchWeb = recipe<
  SearchQuery,
  SearchWebResult | { error: string }
>("Search Web", ({ query }) => {
  const { result, error } = fetchData<SearchWebResult>({
    url: "/api/agent-tools/web-search",
    mode: "json",
    options: {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: {
        query,
        max_results: 5,
      },
    },
  });

  // TODO(seefeld): Should we instead return { result, error }? Or allocate a
  // special [ERROR] for errors? Ideally this isn't specific to using recipes as
  // tools but a general pattern.
  return ifElse(error, { error }, result);
});

/** Read and extract content from a specific webpage URL. */
type ReadWebRequest = {
  /** The URL of the webpage to read and extract content from. */
  url: string;
};

type ReadWebResult = {
  content: string;
  metadata: {
    title?: string;
    author?: string;
    date?: string;
    word_count: number;
  };
};

export const readWebpage = recipe<
  ReadWebRequest,
  ReadWebResult | { error: string }
>("Read Webpage", ({ url }) => {
  const { result, error } = fetchData<ReadWebResult>({
    url: "/api/agent-tools/web-read",
    mode: "json",
    options: {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: {
        url,
        max_tokens: 4000,
        include_code: true,
      },
    },
  });

  return ifElse(error, { error }, result);
});

type ToolsInput = {
  list: ListItem[];
};

export default recipe<ToolsInput>("Tools", ({ list }) => {
  const tools: Record<string, BuiltInLLMTool> = {
    search_web: {
      pattern: searchWeb,
    },
    read_webpage: {
      pattern: readWebpage,
    },
    calculator: {
      pattern: calculator,
    },
    addListItem: {
      handler: addListItem({ list }),
    },
  };

  return { tools, list };
});
"}]}}
